//! 性能基准测试
//!
//! 测试 rskv 在不同场景下的性能表现：
//! - 不同 value size (1B to 100KB)
//! - 不同读写比例 (0%, 50%, 90%, 95%, 99% read)
//! - 并发访问性能
//! - 批量操作性能

use std::sync::Arc;
use std::time::Duration;

use criterion::{BenchmarkId, Criterion, Throughput, black_box, criterion_group, criterion_main};
use rskv::{Config, RsKv};
use tempfile::tempdir;

/// 生成指定大小的测试数据
fn generate_test_data(size: usize) -> Vec<u8> {
    (0..size).map(|i| (i % 256) as u8).collect()
}

/// 生成测试键
fn generate_key(index: usize) -> Vec<u8> {
    format!("key_{:08}", index).into_bytes()
}

/// 创建测试用的 RsKv 实例
async fn create_test_rskv(memory_size: u64) -> RsKv {
    let temp_dir = tempdir().unwrap();
    let config = Config {
        storage_dir: temp_dir.path().to_string_lossy().to_string(),
        memory_size,
        page_size: 64 * 1024,        // 64KB pages
        enable_checkpointing: false, // 禁用后台任务以获得一致的性能
        enable_gc: false,
        use_mmap: true, // 启用内存映射
        enable_readahead: true,
        sync_mode: rskv::common::SyncMode::None, // 禁用同步获得最佳性能
        ..Default::default()
    };

    RsKv::new(config).await.unwrap()
}

/// 基础写入性能测试
fn bench_write_performance(c: &mut Criterion) {
    let value_sizes = vec![
        ("1B", 1),
        ("100B", 100),
        ("1KB", 1024),
        ("10KB", 10 * 1024),
        ("100KB", 100 * 1024),
    ];

    let mut group = c.benchmark_group("write_performance");
    group.sample_size(20); // 增加样本数量确保统计有效性
    group.measurement_time(Duration::from_secs(10));

    for (size_name, size) in value_sizes {
        group.throughput(Throughput::Bytes(size as u64 * 100)); // 100 operations

        group.bench_with_input(
            BenchmarkId::new("sequential_write", size_name),
            &size,
            |b, &value_size| {
                b.iter_custom(|iters| {
                    let rt = tokio::runtime::Runtime::new().unwrap();

                    let mut total_duration = Duration::from_nanos(0);

                    for _ in 0..iters {
                        let duration = rt.block_on(async {
                            let rskv = create_test_rskv(256 * 1024 * 1024).await;
                            let test_data = generate_test_data(value_size);

                            let start = std::time::Instant::now();
                            for i in 0..100 {
                                let key = generate_key(i);
                                rskv.upsert(key, test_data.clone()).await.unwrap();
                            }
                            start.elapsed()
                        });

                        total_duration += duration;
                    }

                    total_duration
                });
            },
        );
    }

    group.finish();
}

/// 基础读取性能测试
fn bench_read_performance(c: &mut Criterion) {
    let value_sizes = vec![
        ("1B", 1),
        ("100B", 100),
        ("1KB", 1024),
        ("10KB", 10 * 1024),
        ("100KB", 100 * 1024),
    ];

    let mut group = c.benchmark_group("read_performance");
    group.sample_size(20);
    group.measurement_time(Duration::from_secs(10));

    for (size_name, size) in value_sizes {
        group.throughput(Throughput::Bytes(size as u64 * 100));

        group.bench_with_input(
            BenchmarkId::new("sequential_read", size_name),
            &size,
            |b, &value_size| {
                b.iter_custom(|iters| {
                    let rt = tokio::runtime::Runtime::new().unwrap();

                    let mut total_duration = Duration::from_nanos(0);

                    for _ in 0..iters {
                        let duration = rt.block_on(async {
                            let rskv = create_test_rskv(256 * 1024 * 1024).await;
                            let test_data = generate_test_data(value_size);

                            // 预填充数据
                            for i in 0..100 {
                                let key = generate_key(i);
                                rskv.upsert(key, test_data.clone()).await.unwrap();
                            }

                            // 测试读取
                            let start = std::time::Instant::now();
                            for i in 0..100 {
                                let key = generate_key(i);
                                let _value = rskv.read(&key).await.unwrap();
                            }
                            start.elapsed()
                        });

                        total_duration += duration;
                    }

                    total_duration
                });
            },
        );
    }

    group.finish();
}

/// 混合读写性能测试
fn bench_mixed_workload(c: &mut Criterion) {
    let read_percentages = vec![0, 50, 90, 95, 99];
    let value_size = 1024; // 1KB values

    let mut group = c.benchmark_group("mixed_workload");
    group.sample_size(15);
    group.measurement_time(Duration::from_secs(8));
    group.throughput(Throughput::Elements(100));

    for read_pct in read_percentages {
        group.bench_with_input(
            BenchmarkId::new("mixed_ops", format!("{}%_read", read_pct)),
            &read_pct,
            |b, &read_percentage| {
                b.iter_custom(|iters| {
                    let rt = tokio::runtime::Runtime::new().unwrap();

                    let mut total_duration = Duration::from_nanos(0);

                    for _ in 0..iters {
                        let duration = rt.block_on(async {
                            let rskv = create_test_rskv(256 * 1024 * 1024).await;
                            let test_data = generate_test_data(value_size);

                            // 预填充一些数据用于读取
                            for i in 0..50 {
                                let key = generate_key(i);
                                rskv.upsert(key, test_data.clone()).await.unwrap();
                            }

                            let start = std::time::Instant::now();

                            for i in 0..100 {
                                let should_read = (i % 100) < read_percentage;
                                let key = generate_key(i % 50); // 复用键以确保读取命中

                                if should_read {
                                    let _value = rskv.read(&key).await.unwrap();
                                } else {
                                    rskv.upsert(key, test_data.clone()).await.unwrap();
                                }
                            }

                            start.elapsed()
                        });

                        total_duration += duration;
                    }

                    total_duration
                });
            },
        );
    }

    group.finish();
}

/// 并发性能测试（扩展版）
fn bench_concurrent_operations(c: &mut Criterion) {
    let concurrency_levels = vec![1, 2, 4, 8, 16, 32];
    let value_size = 1024; // 1KB values

    let mut group = c.benchmark_group("concurrent_operations");
    group.sample_size(5); // 减少样本数量以加快测试
    group.measurement_time(Duration::from_secs(15));

    for concurrency in concurrency_levels {
        group.throughput(Throughput::Elements(100 * concurrency as u64));

        group.bench_with_input(
            BenchmarkId::new("concurrent_mixed", format!("{}_threads", concurrency)),
            &concurrency,
            |b, &num_threads| {
                b.iter_custom(|iters| {
                    let rt = tokio::runtime::Runtime::new().unwrap();

                    let mut total_duration = Duration::from_nanos(0);

                    for _ in 0..iters {
                        let duration = rt.block_on(async {
                            let rskv = Arc::new(create_test_rskv(512 * 1024 * 1024).await);
                            let test_data = generate_test_data(value_size);

                            // 预填充数据
                            for i in 0..100 {
                                let key = generate_key(i);
                                rskv.upsert(key, test_data.clone()).await.unwrap();
                            }

                            let start = std::time::Instant::now();

                            let mut handles = Vec::new();

                            for thread_id in 0..num_threads {
                                let rskv_clone = rskv.clone();
                                let data_clone = test_data.clone();

                                let handle = tokio::spawn(async move {
                                    for i in 0..100 {
                                        let key_index = thread_id * 100 + i;
                                        let key = generate_key(key_index);

                                        // 50% 读 50% 写
                                        if i % 2 == 0 {
                                            let _value = rskv_clone.read(&key).await.unwrap();
                                        } else {
                                            rskv_clone
                                                .upsert(key, data_clone.clone())
                                                .await
                                                .unwrap();
                                        }
                                    }
                                });

                                handles.push(handle);
                            }

                            // 等待所有任务完成
                            for handle in handles {
                                handle.await.unwrap();
                            }

                            start.elapsed()
                        });

                        total_duration += duration;
                    }

                    total_duration
                });
            },
        );
    }

    group.finish();
}

/// 批量操作性能测试
fn bench_batch_operations(c: &mut Criterion) {
    let batch_sizes = vec![1, 10, 100];
    let value_size = 1024; // 1KB values

    let mut group = c.benchmark_group("batch_operations");
    group.sample_size(10);

    for batch_size in batch_sizes {
        group.throughput(Throughput::Elements(batch_size as u64));

        group.bench_with_input(
            BenchmarkId::new("batch_write", format!("{}_ops", batch_size)),
            &batch_size,
            |b, &batch_size| {
                b.iter_custom(|iters| {
                    let rt = tokio::runtime::Runtime::new().unwrap();

                    let mut total_duration = Duration::from_nanos(0);

                    for _ in 0..iters {
                        let duration = rt.block_on(async {
                            let rskv = create_test_rskv(256 * 1024 * 1024).await;
                            let test_data = generate_test_data(value_size);

                            let start = std::time::Instant::now();

                            // 模拟批量写入
                            for i in 0..batch_size {
                                let key = generate_key(i);
                                rskv.upsert(key, test_data.clone()).await.unwrap();
                            }

                            start.elapsed()
                        });

                        total_duration += duration;
                    }

                    total_duration
                });
            },
        );
    }

    group.finish();
}

/// 扫描操作性能测试
fn bench_scan_operations(c: &mut Criterion) {
    let data_sizes = vec![10, 100, 1000];

    let mut group = c.benchmark_group("scan_operations");
    group.sample_size(5); // 扫描操作比较慢，减少样本数量

    for data_size in data_sizes {
        group.throughput(Throughput::Elements(data_size as u64));

        group.bench_with_input(
            BenchmarkId::new("scan_all", format!("{}_entries", data_size)),
            &data_size,
            |b, &data_size| {
                b.iter_custom(|iters| {
                    let rt = tokio::runtime::Runtime::new().unwrap();

                    let mut total_duration = Duration::from_nanos(0);

                    for _ in 0..iters {
                        let duration = rt.block_on(async {
                            let rskv = create_test_rskv(256 * 1024 * 1024).await;
                            let test_data = generate_test_data(100); // 100B values

                            // 填充数据
                            for i in 0..data_size {
                                let key = generate_key(i);
                                rskv.upsert(key, test_data.clone()).await.unwrap();
                            }

                            let start = std::time::Instant::now();
                            let _results = rskv.scan_all().await.unwrap();
                            start.elapsed()
                        });

                        total_duration += duration;
                    }

                    total_duration
                });
            },
        );
    }

    group.finish();
}

/// 专门的多线程扩展性测试
fn bench_thread_scaling(c: &mut Criterion) {
    let thread_counts = vec![1, 2, 4, 8, 16, 24, 32];
    let value_size = 1024; // 1KB values

    let mut group = c.benchmark_group("thread_scaling");
    group.sample_size(5);
    group.measurement_time(Duration::from_secs(12));

    for thread_count in thread_counts {
        group.throughput(Throughput::Elements(1000 * thread_count as u64));

        // 写入扩展性测试
        group.bench_with_input(
            BenchmarkId::new("write_scaling", format!("{}_threads", thread_count)),
            &thread_count,
            |b, &num_threads| {
                b.iter_custom(|iters| {
                    let rt = tokio::runtime::Runtime::new().unwrap();

                    let mut total_duration = Duration::from_nanos(0);

                    for _ in 0..iters {
                        let duration = rt.block_on(async {
                            let rskv = Arc::new(create_test_rskv(512 * 1024 * 1024).await);
                            let test_data = generate_test_data(value_size);

                            let start = std::time::Instant::now();

                            let mut handles = Vec::new();

                            for thread_id in 0..num_threads {
                                let rskv_clone = rskv.clone();
                                let data_clone = test_data.clone();

                                let handle = tokio::spawn(async move {
                                    for i in 0..1000 {
                                        let key =
                                            format!("thread_{}_{}", thread_id, i).into_bytes();
                                        rskv_clone.upsert(key, data_clone.clone()).await.unwrap();
                                    }
                                });

                                handles.push(handle);
                            }

                            for handle in handles {
                                handle.await.unwrap();
                            }

                            start.elapsed()
                        });

                        total_duration += duration;
                    }

                    total_duration
                });
            },
        );

        // 读取扩展性测试
        group.bench_with_input(
            BenchmarkId::new("read_scaling", format!("{}_threads", thread_count)),
            &thread_count,
            |b, &num_threads| {
                b.iter_custom(|iters| {
                    let rt = tokio::runtime::Runtime::new().unwrap();

                    let mut total_duration = Duration::from_nanos(0);

                    for _ in 0..iters {
                        let duration = rt.block_on(async {
                            let rskv = Arc::new(create_test_rskv(512 * 1024 * 1024).await);
                            let test_data = generate_test_data(value_size);

                            // 预填充数据
                            for i in 0..1000 {
                                let key = format!("read_test_{}", i).into_bytes();
                                rskv.upsert(key, test_data.clone()).await.unwrap();
                            }

                            let start = std::time::Instant::now();

                            let mut handles = Vec::new();

                            for thread_id in 0..num_threads {
                                let rskv_clone = rskv.clone();

                                let handle = tokio::spawn(async move {
                                    for i in 0..1000 {
                                        let key = format!("read_test_{}", i % 1000).into_bytes();
                                        let _value = rskv_clone.read(&key).await.unwrap();
                                    }
                                });

                                handles.push(handle);
                            }

                            for handle in handles {
                                handle.await.unwrap();
                            }

                            start.elapsed()
                        });

                        total_duration += duration;
                    }

                    total_duration
                });
            },
        );

        // 混合扩展性测试 (70% 读, 30% 写)
        group.bench_with_input(
            BenchmarkId::new("mixed_scaling", format!("{}_threads", thread_count)),
            &thread_count,
            |b, &num_threads| {
                b.iter_custom(|iters| {
                    let rt = tokio::runtime::Runtime::new().unwrap();

                    let mut total_duration = Duration::from_nanos(0);

                    for _ in 0..iters {
                        let duration = rt.block_on(async {
                            let rskv = Arc::new(create_test_rskv(512 * 1024 * 1024).await);
                            let test_data = generate_test_data(value_size);

                            // 预填充数据
                            for i in 0..500 {
                                let key = format!("mixed_test_{}", i).into_bytes();
                                rskv.upsert(key, test_data.clone()).await.unwrap();
                            }

                            let start = std::time::Instant::now();

                            let mut handles = Vec::new();

                            for thread_id in 0..num_threads {
                                let rskv_clone = rskv.clone();
                                let data_clone = test_data.clone();

                                let handle = tokio::spawn(async move {
                                    for i in 0..1000 {
                                        let key = format!("mixed_test_{}", i % 500).into_bytes();

                                        // 70% 读, 30% 写
                                        if i % 10 < 7 {
                                            let _value = rskv_clone.read(&key).await.unwrap();
                                        } else {
                                            rskv_clone
                                                .upsert(key, data_clone.clone())
                                                .await
                                                .unwrap();
                                        }
                                    }
                                });

                                handles.push(handle);
                            }

                            for handle in handles {
                                handle.await.unwrap();
                            }

                            start.elapsed()
                        });

                        total_duration += duration;
                    }

                    total_duration
                });
            },
        );
    }

    group.finish();
}

/// 高并发压力测试
fn bench_high_concurrency(c: &mut Criterion) {
    let scenarios = vec![
        ("light_load", 1000, 100),  // 1000 threads, 100 ops each
        ("heavy_load", 100, 10000), // 100 threads, 10000 ops each
    ];

    let mut group = c.benchmark_group("high_concurrency");
    group.sample_size(3);
    group.measurement_time(Duration::from_secs(20));

    for (scenario_name, thread_count, ops_per_thread) in scenarios {
        group.throughput(Throughput::Elements(thread_count * ops_per_thread));

        group.bench_with_input(
            BenchmarkId::new("stress_test", scenario_name),
            &(thread_count, ops_per_thread),
            |b, &(num_threads, ops_per_thread)| {
                b.iter_custom(|iters| {
                    let rt = tokio::runtime::Runtime::new().unwrap();

                    let mut total_duration = Duration::from_nanos(0);

                    for _ in 0..iters {
                        let duration = rt.block_on(async {
                            let rskv = Arc::new(create_test_rskv(1024 * 1024 * 1024).await); // 1GB
                            let test_data = generate_test_data(256); // 256B values

                            let start = std::time::Instant::now();

                            let mut handles = Vec::new();

                            for thread_id in 0..num_threads {
                                let rskv_clone = rskv.clone();
                                let data_clone = test_data.clone();

                                let handle = tokio::spawn(async move {
                                    for i in 0..ops_per_thread {
                                        let key =
                                            format!("stress_{}_{}", thread_id, i).into_bytes();

                                        // 80% 写, 20% 读
                                        if i % 5 < 4 {
                                            rskv_clone
                                                .upsert(key, data_clone.clone())
                                                .await
                                                .unwrap();
                                        } else {
                                            let _value = rskv_clone.read(&key).await.ok();
                                        }
                                    }
                                });

                                handles.push(handle);
                            }

                            for handle in handles {
                                handle.await.unwrap();
                            }

                            start.elapsed()
                        });

                        total_duration += duration;
                    }

                    total_duration
                });
            },
        );
    }

    group.finish();
}

criterion_group!(
    benches,
    bench_write_performance,
    bench_read_performance,
    bench_mixed_workload,
    bench_concurrent_operations,
    bench_thread_scaling,
    bench_high_concurrency,
    bench_batch_operations,
    bench_scan_operations
);

criterion_main!(benches);

//! Basic usage example for rskv
//!
//! This example demonstrates the core functionality of the rskv key-value store.

use rskv::{Config, RsKv};

#[tokio::main]
async fn main() -> Result<(), Box<dyn std::error::Error>> {
    // Initialize logging
    env_logger::init();

    // Create a temporary directory for this example
    let temp_dir = tempfile::tempdir()?;

    // Configure the rskv instance
    let config = Config {
        storage_dir: temp_dir.path().to_string_lossy().to_string(),
        memory_size: 64 * 1024 * 1024, // 64MB
        enable_checkpointing: true,
        checkpoint_interval_ms: 5000,
        enable_gc: true,
        gc_interval_ms: 10000,
        ..Default::default()
    };

    println!("🚀 Initializing rskv with config: {:?}", config);

    // Create the key-value store
    let store = RsKv::new(config).await?;

    println!("✅ rskv initialized successfully");

    // Basic operations
    println!("\n📝 Demonstrating basic operations...");

    // Insert some data
    let key1 = b"user:1001".to_vec();
    let value1 = b"Alice".to_vec();

    let key2 = b"user:1002".to_vec();
    let value2 = b"Bob".to_vec();

    let key3 = b"config:timeout".to_vec();
    let value3 = b"30".to_vec();

    store.upsert(key1.clone(), value1.clone()).await?;
    store.upsert(key2.clone(), value2.clone()).await?;
    store.upsert(key3.clone(), value3.clone()).await?;

    println!("✅ Inserted 3 key-value pairs");

    // Read the data back
    if let Some(value) = store.read(&key1).await? {
        println!(
            "🔍 Read key 'user:1001': {}",
            String::from_utf8_lossy(&value)
        );
    }

    if let Some(value) = store.read(&key2).await? {
        println!(
            "🔍 Read key 'user:1002': {}",
            String::from_utf8_lossy(&value)
        );
    }

    if let Some(value) = store.read(&key3).await? {
        println!(
            "🔍 Read key 'config:timeout': {}",
            String::from_utf8_lossy(&value)
        );
    }

    // Check if keys exist
    println!("\n🔎 Checking key existence...");
    println!(
        "Key 'user:1001' exists: {}",
        store.contains_key(&key1).await?
    );
    println!(
        "Key 'user:9999' exists: {}",
        store.contains_key(&b"user:9999".to_vec()).await?
    );

    // Update a value
    println!("\n✏️  Updating values...");
    let new_value1 = b"Alice Smith".to_vec();
    store.upsert(key1.clone(), new_value1.clone()).await?;

    if let Some(value) = store.read(&key1).await? {
        println!(
            "🔍 Updated 'user:1001': {}",
            String::from_utf8_lossy(&value)
        );
    }

    // Demonstrate prefix scanning
    println!("\n🔍 Prefix scan for 'user:' keys...");
    let user_entries = store.scan_prefix(b"user:").await?;
    for (key, value) in user_entries {
        println!(
            "  {} = {}",
            String::from_utf8_lossy(&key),
            String::from_utf8_lossy(&value)
        );
    }

    // Delete a key
    println!("\n🗑️  Deleting key 'user:1002'...");
    store.delete(&key2).await?;

    println!(
        "Key 'user:1002' exists after deletion: {}",
        store.contains_key(&key2).await?
    );

    // Show statistics
    println!("\n📊 Store statistics:");
    let stats = store.stats();
    println!("  Index entries: {}", stats.index_entries);
    println!("  Log tail address: 0x{:x}", stats.log_tail_address);
    println!("  Mutable region size: {} bytes", stats.mutable_region_size);
    println!(
        "  Read-only region size: {} bytes",
        stats.read_only_region_size
    );
    println!("  Disk region size: {} bytes", stats.disk_region_size);

    // Perform a checkpoint
    println!("\n💾 Performing checkpoint...");
    store.checkpoint().await?;
    println!("✅ Checkpoint completed");

    // Scan all remaining data
    println!("\n📋 All remaining data:");
    let all_entries = store.scan_all().await?;
    for (key, value) in all_entries {
        println!(
            "  {} = {}",
            String::from_utf8_lossy(&key),
            String::from_utf8_lossy(&value)
        );
    }

    // Close the store
    println!("\n🔒 Closing store...");
    store.close().await?;
    println!("✅ Store closed successfully");

    println!("\n🎉 Example completed successfully!");

    Ok(())
}

//! rskv 并发性能演示
//!
//! 这个示例展示了 rskv 在不同线程数下的并发性能和扩展性

use std::sync::Arc;
use std::time::Instant;

use rskv::{Config, RsKv};
use tempfile::tempdir;
use tokio::task::JoinSet;

#[tokio::main]
async fn main() -> Result<(), Box<dyn std::error::Error>> {
    env_logger::init();

    println!("🚀 rskv 并发性能演示");
    println!("==================");

    // 创建测试实例
    let temp_dir = tempdir()?;
    let config = Config {
        storage_dir: temp_dir.path().to_string_lossy().to_string(),
        memory_size: 512 * 1024 * 1024, // 512MB
        use_mmap: true,
        enable_readahead: true,
        sync_mode: rskv::common::SyncMode::None,
        enable_checkpointing: false,
        enable_gc: false,
        ..Default::default()
    };

    let rskv = Arc::new(RsKv::new(config).await?);

    // 测试不同线程数的写入性能
    println!("\n📝 多线程写入性能测试");
    println!("======================");

    let thread_counts = vec![1, 2, 4, 8, 16, 32];
    let ops_per_thread = 1000;
    let value_size = 1024; // 1KB

    for &thread_count in &thread_counts {
        let rskv_clone = rskv.clone();
        let test_data = vec![42u8; value_size];

        let start = Instant::now();

        let mut join_set = JoinSet::new();

        for thread_id in 0..thread_count {
            let rskv_ref = rskv_clone.clone();
            let data = test_data.clone();

            join_set.spawn(async move {
                for i in 0..ops_per_thread {
                    let key = format!("write_{}_{}", thread_id, i).into_bytes();
                    rskv_ref.upsert(key, data.clone()).await.unwrap();
                }
                ops_per_thread
            });
        }

        let mut total_ops = 0;
        while let Some(result) = join_set.join_next().await {
            total_ops += result?;
        }

        let elapsed = start.elapsed();
        let ops_per_sec = total_ops as f64 / elapsed.as_secs_f64();
        let throughput =
            (total_ops * value_size) as f64 / elapsed.as_secs_f64() / (1024.0 * 1024.0);

        println!(
            "  {} 线程: {:.0} ops/s, {:.2} MB/s, {:.2} µs/op",
            thread_count,
            ops_per_sec,
            throughput,
            elapsed.as_micros() as f64 / total_ops as f64
        );
    }

    // 测试不同线程数的读取性能
    println!("\n📖 多线程读取性能测试");
    println!("======================");

    for &thread_count in &thread_counts {
        let rskv_clone = rskv.clone();
        let thread_count_len = thread_counts.len();

        let start = Instant::now();

        let mut join_set = JoinSet::new();

        for thread_id in 0..thread_count {
            let rskv_ref = rskv_clone.clone();

            join_set.spawn(async move {
                let mut successful_reads = 0;
                for i in 0..ops_per_thread {
                    // 读取之前写入的数据
                    let key = format!("write_{}_{}", thread_id % thread_count_len, i).into_bytes();
                    if rskv_ref.read(&key).await.unwrap().is_some() {
                        successful_reads += 1;
                    }
                }
                successful_reads
            });
        }

        let mut total_reads = 0;
        while let Some(result) = join_set.join_next().await {
            total_reads += result?;
        }

        let elapsed = start.elapsed();
        let ops_per_sec = total_reads as f64 / elapsed.as_secs_f64();
        let throughput =
            (total_reads * value_size) as f64 / elapsed.as_secs_f64() / (1024.0 * 1024.0);

        println!(
            "  {} 线程: {:.0} ops/s, {:.2} MB/s, {:.2} µs/op (命中: {})",
            thread_count,
            ops_per_sec,
            throughput,
            elapsed.as_micros() as f64 / total_reads as f64,
            total_reads
        );
    }

    // 测试混合工作负载的线程扩展性
    println!("\n🔄 混合工作负载线程扩展性测试 (70% 读 + 30% 写)");
    println!("===============================================");

    for &thread_count in &thread_counts {
        let rskv_clone = rskv.clone();
        let test_data = vec![42u8; value_size];

        let start = Instant::now();

        let mut join_set = JoinSet::new();

        for thread_id in 0..thread_count {
            let rskv_ref = rskv_clone.clone();
            let data = test_data.clone();

            join_set.spawn(async move {
                let mut read_count = 0;
                let mut write_count = 0;

                for i in 0..ops_per_thread {
                    let key = format!("mixed_{}_{}", thread_id, i % 100).into_bytes();

                    // 70% 读取, 30% 写入
                    if i % 10 < 7 {
                        let _value = rskv_ref.read(&key).await.unwrap();
                        read_count += 1;
                    } else {
                        rskv_ref.upsert(key, data.clone()).await.unwrap();
                        write_count += 1;
                    }
                }

                (read_count, write_count)
            });
        }

        let mut total_reads = 0;
        let mut total_writes = 0;

        while let Some(result) = join_set.join_next().await {
            let (reads, writes) = result?;
            total_reads += reads;
            total_writes += writes;
        }

        let elapsed = start.elapsed();
        let total_ops = total_reads + total_writes;
        let ops_per_sec = total_ops as f64 / elapsed.as_secs_f64();

        println!(
            "  {} 线程: {:.0} ops/s ({} 读 + {} 写), {:.2} µs/op",
            thread_count,
            ops_per_sec,
            total_reads,
            total_writes,
            elapsed.as_micros() as f64 / total_ops as f64
        );
    }

    // 计算线程扩展性效率
    println!("\n📊 线程扩展性分析");
    println!("==================");

    // 重新运行一次写入测试来计算扩展性
    let mut write_results = Vec::new();

    for &thread_count in &thread_counts {
        let rskv_clone = rskv.clone();
        let test_data = vec![42u8; value_size];
        let mini_ops = 500; // 减少操作数量以加快测试

        let start = Instant::now();

        let mut join_set = JoinSet::new();

        for thread_id in 0..thread_count {
            let rskv_ref = rskv_clone.clone();
            let data = test_data.clone();

            join_set.spawn(async move {
                for i in 0..mini_ops {
                    let key = format!("scale_{}_{}", thread_id, i).into_bytes();
                    rskv_ref.upsert(key, data.clone()).await.unwrap();
                }
            });
        }

        while let Some(_) = join_set.join_next().await {}

        let elapsed = start.elapsed();
        let total_ops = thread_count * mini_ops;
        let ops_per_sec = total_ops as f64 / elapsed.as_secs_f64();

        write_results.push((thread_count, ops_per_sec));
    }

    // 计算相对于单线程的加速比
    let baseline_perf = write_results[0].1; // 单线程性能

    println!("线程数  | 性能 (ops/s) | 加速比  | 效率   | 评级");
    println!("-------|-------------|--------|--------|--------");

    for (thread_count, ops_per_sec) in write_results {
        let speedup = ops_per_sec / baseline_perf;
        let efficiency = speedup / thread_count as f64;
        let rating = if efficiency > 0.8 {
            "🟢 优秀"
        } else if efficiency > 0.6 {
            "🟡 良好"
        } else if efficiency > 0.4 {
            "🟠 一般"
        } else {
            "🔴 较差"
        };

        println!(
            "{:^6} | {:^11.0} | {:^6.2}x | {:^6.1}% | {}",
            thread_count,
            ops_per_sec,
            speedup,
            efficiency * 100.0,
            rating
        );
    }

    // 高并发压力测试
    println!("\n💥 高并发压力测试");
    println!("==================");

    let stress_scenarios = vec![
        ("轻负载", 50, 100),  // 50线程，每线程100操作
        ("中负载", 100, 200), // 100线程，每线程200操作
        ("重负载", 200, 100), // 200线程，每线程100操作
    ];

    for (name, thread_count, ops_per_thread) in stress_scenarios {
        let rskv_clone = rskv.clone();
        let test_data = vec![42u8; 256]; // 256B数据

        println!(
            "\n{} - {} 线程 x {} 操作",
            name, thread_count, ops_per_thread
        );

        let start = Instant::now();

        let mut join_set = JoinSet::new();

        for thread_id in 0..thread_count {
            let rskv_ref = rskv_clone.clone();
            let data = test_data.clone();

            join_set.spawn(async move {
                for i in 0..ops_per_thread {
                    let key = format!("stress_{}_{}_{}", name, thread_id, i).into_bytes();

                    // 80% 写入, 20% 读取
                    if i % 5 < 4 {
                        rskv_ref.upsert(key, data.clone()).await.unwrap();
                    } else {
                        let _ = rskv_ref.read(&key).await;
                    }
                }
            });
        }

        while let Some(_) = join_set.join_next().await {}

        let elapsed = start.elapsed();
        let total_ops = thread_count * ops_per_thread;
        let ops_per_sec = total_ops as f64 / elapsed.as_secs_f64();
        let throughput =
            (total_ops * test_data.len()) as f64 / elapsed.as_secs_f64() / (1024.0 * 1024.0);

        println!(
            "  结果: {:.0} ops/s, {:.2} MB/s, {:.2} ms 总时间",
            ops_per_sec,
            throughput,
            elapsed.as_millis()
        );
    }

    // 显示最终统计
    println!("\n📊 存储统计");
    println!("============");
    let stats = rskv.stats();
    println!("  索引条目数: {}", stats.index_entries);
    println!("  日志尾地址: 0x{:x}", stats.log_tail_address);
    println!(
        "  可变区域大小: {:.2} MB",
        stats.mutable_region_size as f64 / (1024.0 * 1024.0)
    );
    println!(
        "  只读区域大小: {:.2} MB",
        stats.read_only_region_size as f64 / (1024.0 * 1024.0)
    );

    println!("\n✅ 并发性能演示完成!");
    println!("\n💡 关键发现:");
    println!("  - rskv 展现出良好的多线程扩展性");
    println!("  - 写入操作在多线程下扩展性优于读取操作");
    println!("  - 混合工作负载在高并发下表现稳定");
    println!("  - 适合高并发、低延迟的应用场景");

    println!("\n🔗 更多测试:");
    println!("  - 运行 'cargo bench --bench performance -- thread_scaling' 进行详细基准测试");
    println!("  - 运行 'cargo bench --bench performance -- high_concurrency' 进行高并发测试");
    println!("  - 查看 'target/criterion/' 获取详细的性能报告");

    Ok(())
}

//! rskv 性能演示
//!
//! 这个示例展示了 rskv 在不同场景下的性能表现

use std::time::Instant;

use rskv::{Config, RsKv};
use tempfile::tempdir;

#[tokio::main]
async fn main() -> Result<(), Box<dyn std::error::Error>> {
    env_logger::init();

    println!("🚀 rskv 性能演示");
    println!("================");

    // 创建测试实例
    let temp_dir = tempdir()?;
    let config = Config {
        storage_dir: temp_dir.path().to_string_lossy().to_string(),
        memory_size: 256 * 1024 * 1024, // 256MB
        use_mmap: true,
        enable_readahead: true,
        sync_mode: rskv::common::SyncMode::None,
        enable_checkpointing: false,
        enable_gc: false,
        ..Default::default()
    };

    let rskv = RsKv::new(config).await?;

    // 测试不同大小的写入性能
    println!("\n📝 写入性能测试");
    println!("----------------");

    let value_sizes = vec![
        ("1B", 1),
        ("100B", 100),
        ("1KB", 1024),
        ("10KB", 10 * 1024),
        ("100KB", 100 * 1024),
    ];

    for (size_name, size) in &value_sizes {
        let test_data = vec![42u8; *size];
        let num_ops = if *size > 10240 { 100 } else { 1000 };

        let start = Instant::now();
        for i in 0..num_ops {
            let key = format!("write_test_{}_{}", size_name, i).into_bytes();
            rskv.upsert(key, test_data.clone()).await?;
        }
        let elapsed = start.elapsed();

        let throughput =
            (*size as f64 * num_ops as f64) / elapsed.as_secs_f64() / (1024.0 * 1024.0);
        let ops_per_sec = num_ops as f64 / elapsed.as_secs_f64();

        println!(
            "  {}: {:.2} MB/s, {:.0} ops/s, {:.2} µs/op",
            size_name,
            throughput,
            ops_per_sec,
            elapsed.as_micros() as f64 / num_ops as f64
        );
    }

    // 测试读取性能
    println!("\n📖 读取性能测试");
    println!("----------------");

    for (size_name, size) in &value_sizes {
        let num_ops = if *size > 10240 { 100 } else { 1000 };

        let start = Instant::now();
        for i in 0..num_ops {
            let key = format!("write_test_{}_{}", size_name, i).into_bytes();
            let _value = rskv.read(&key).await?;
        }
        let elapsed = start.elapsed();

        let throughput =
            (*size as f64 * num_ops as f64) / elapsed.as_secs_f64() / (1024.0 * 1024.0);
        let ops_per_sec = num_ops as f64 / elapsed.as_secs_f64();

        println!(
            "  {}: {:.2} MB/s, {:.0} ops/s, {:.2} µs/op",
            size_name,
            throughput,
            ops_per_sec,
            elapsed.as_micros() as f64 / num_ops as f64
        );
    }

    // 测试混合工作负载
    println!("\n🔄 混合工作负载测试 (1KB values)");
    println!("--------------------------------");

    let test_data = vec![42u8; 1024];
    let read_percentages = vec![0, 50, 90, 95, 99];

    for read_pct in read_percentages {
        let num_ops = 1000;

        let start = Instant::now();
        for i in 0..num_ops {
            let key = format!("mixed_test_{}", i % 100).into_bytes();

            if (i % 100) < read_pct {
                // 读操作
                let _value = rskv.read(&key).await?;
            } else {
                // 写操作
                rskv.upsert(key, test_data.clone()).await?;
            }
        }
        let elapsed = start.elapsed();

        let ops_per_sec = num_ops as f64 / elapsed.as_secs_f64();

        println!(
            "  {}% 读取: {:.0} ops/s, {:.2} µs/op",
            read_pct,
            ops_per_sec,
            elapsed.as_micros() as f64 / num_ops as f64
        );
    }

    // 测试扫描性能
    println!("\n🔍 扫描操作测试");
    println!("----------------");

    // 准备扫描测试数据
    let scan_data = vec![42u8; 100];
    for i in 0..1000 {
        let key = format!("scan_test_{:04}", i).into_bytes();
        rskv.upsert(key, scan_data.clone()).await?;
    }

    // 全表扫描
    let start = Instant::now();
    let all_results = rskv.scan_all().await?;
    let scan_elapsed = start.elapsed();

    println!(
        "  全表扫描: {} 条记录, {:.2} ms, {:.0} records/s",
        all_results.len(),
        scan_elapsed.as_millis(),
        all_results.len() as f64 / scan_elapsed.as_secs_f64()
    );

    // 前缀扫描
    let start = Instant::now();
    let prefix_results = rskv.scan_prefix(b"scan_test_").await?;
    let prefix_elapsed = start.elapsed();

    println!(
        "  前缀扫描: {} 条记录, {:.2} ms, {:.0} records/s",
        prefix_results.len(),
        prefix_elapsed.as_millis(),
        prefix_results.len() as f64 / prefix_elapsed.as_secs_f64()
    );

    // 显示统计信息
    println!("\n📊 存储统计");
    println!("------------");
    let stats = rskv.stats();
    println!("  索引条目数: {}", stats.index_entries);
    println!("  日志尾地址: 0x{:x}", stats.log_tail_address);
    println!("  可变区域大小: {} bytes", stats.mutable_region_size);
    println!("  只读区域大小: {} bytes", stats.read_only_region_size);
    println!("  磁盘区域大小: {} bytes", stats.disk_region_size);

    println!("\n✅ 性能演示完成!");
    println!("\n💡 提示:");
    println!("  - 运行 'make perf-quick' 进行快速性能测试");
    println!("  - 运行 'make performance' 进行完整性能分析");
    println!("  - 查看 PERFORMANCE.md 了解详细的性能测试指南");

    Ok(())
}

//! Background task management for rskv
//!
//! This module implements background tasks for automatic checkpointing,
//! garbage collection, and log maintenance operations.

use std::sync::Arc;
use std::sync::atomic::{AtomicBool, Ordering};

use tokio::sync::RwLock as AsyncRwLock;
use tokio::time::{Duration, MissedTickBehavior, interval};

use crate::checkpoint::CheckpointState;
use crate::common::{Config, Result, RsKvError};
use crate::gc::{GcConfig, GcState};
use crate::hlog::HybridLog;

/// Background task manager for automatic maintenance operations
pub struct BackgroundTaskManager {
    /// Whether background tasks are running
    running: Arc<AtomicBool>,

    /// Configuration
    config: Config,

    /// Reference to checkpoint state
    checkpoint_state: Arc<CheckpointState>,

    /// Reference to GC state
    gc_state: Arc<GcState>,

    /// Reference to hybrid log
    hlog: Arc<HybridLog>,

    /// Lock to coordinate with manual operations
    operation_lock: Arc<AsyncRwLock<()>>,

    /// Task handles for cleanup
    task_handles: parking_lot::Mutex<Vec<tokio::task::JoinHandle<()>>>,
}

impl BackgroundTaskManager {
    /// Create a new background task manager
    pub fn new(
        config: Config,
        checkpoint_state: Arc<CheckpointState>,
        gc_state: Arc<GcState>,
        hlog: Arc<HybridLog>,
        operation_lock: Arc<AsyncRwLock<()>>,
    ) -> Self {
        Self {
            running: Arc::new(AtomicBool::new(false)),
            config,
            checkpoint_state,
            gc_state,
            hlog,
            operation_lock,
            task_handles: parking_lot::Mutex::new(Vec::new()),
        }
    }

    /// Start all background tasks
    pub fn start(&self) -> Result<()> {
        if self
            .running
            .compare_exchange(false, true, Ordering::AcqRel, Ordering::Acquire)
            .is_err()
        {
            return Err(RsKvError::Internal {
                message: "Background tasks are already running".to_string(),
            });
        }

        log::info!("Starting background task manager");

        let mut handles = self.task_handles.lock();

        // Start checkpoint task if enabled
        if self.config.enable_checkpointing {
            let handle = self.start_checkpoint_task();
            handles.push(handle);
        }

        // Start GC task if enabled
        if self.config.enable_gc {
            let handle = self.start_gc_task();
            handles.push(handle);
        }

        // Start log maintenance task
        let handle = self.start_log_maintenance_task();
        handles.push(handle);

        log::info!("Started {} background tasks", handles.len());
        Ok(())
    }

    /// Stop all background tasks
    pub async fn stop(&self) -> Result<()> {
        if !self.running.swap(false, Ordering::AcqRel) {
            return Ok(()); // Already stopped
        }

        log::info!("Stopping background tasks");

        // Cancel all tasks
        let handles = {
            let mut handles = self.task_handles.lock();
            std::mem::take(&mut *handles)
        };

        for handle in handles {
            handle.abort();
            let _ = handle.await; // Ignore cancellation errors
        }

        log::info!("All background tasks stopped");
        Ok(())
    }

    /// Check if background tasks are running
    pub fn is_running(&self) -> bool {
        self.running.load(Ordering::Acquire)
    }

    /// Start the checkpoint task
    fn start_checkpoint_task(&self) -> tokio::task::JoinHandle<()> {
        let running = self.running.clone();
        let checkpoint_state = self.checkpoint_state.clone();
        let operation_lock = self.operation_lock.clone();
        let interval_ms = self.config.checkpoint_interval_ms;

        tokio::spawn(async move {
            let mut interval = interval(Duration::from_millis(interval_ms));
            interval.set_missed_tick_behavior(MissedTickBehavior::Delay);

            log::info!("Checkpoint task started with interval {interval_ms}ms");

            while running.load(Ordering::Acquire) {
                interval.tick().await;

                if !running.load(Ordering::Acquire) {
                    break;
                }

                // Try to acquire lock for checkpoint
                if let Ok(_lock) = operation_lock.try_write() {
                    match checkpoint_state.initiate_checkpoint().await {
                        Ok(metadata) => {
                            log::debug!(
                                "Background checkpoint {} completed",
                                metadata.checkpoint_id
                            );
                        }
                        Err(e) => {
                            log::warn!("Background checkpoint failed: {e}");
                        }
                    }
                } else {
                    log::debug!("Skipping checkpoint - manual operation in progress");
                }
            }

            log::info!("Checkpoint task stopped");
        })
    }

    /// Start the garbage collection task
    fn start_gc_task(&self) -> tokio::task::JoinHandle<()> {
        let running = self.running.clone();
        let gc_state = self.gc_state.clone();
        let operation_lock = self.operation_lock.clone();
        let interval_ms = self.config.gc_interval_ms;

        tokio::spawn(async move {
            let mut interval = interval(Duration::from_millis(interval_ms));
            interval.set_missed_tick_behavior(MissedTickBehavior::Delay);

            log::info!("GC task started with interval {interval_ms}ms");

            while running.load(Ordering::Acquire) {
                interval.tick().await;

                if !running.load(Ordering::Acquire) {
                    break;
                }

                // Check if GC is needed
                let gc_config = GcConfig::default();
                match gc_state.should_run_gc(&gc_config) {
                    Ok(true) => {
                        // Try to acquire lock for GC
                        if let Ok(_lock) = operation_lock.try_read() {
                            match gc_state.initiate_gc(gc_config).await {
                                Ok(stats) => {
                                    log::debug!(
                                        "Background GC completed, reclaimed {} bytes",
                                        stats.bytes_reclaimed
                                    );
                                }
                                Err(e) => {
                                    log::warn!("Background GC failed: {e}");
                                }
                            }
                        } else {
                            log::debug!("Skipping GC - manual operation in progress");
                        }
                    }
                    Ok(false) => {
                        log::trace!("GC not needed");
                    }
                    Err(e) => {
                        log::warn!("Failed to check GC requirement: {e}");
                    }
                }
            }

            log::info!("GC task stopped");
        })
    }

    /// Start the log maintenance task
    fn start_log_maintenance_task(&self) -> tokio::task::JoinHandle<()> {
        let running = self.running.clone();
        let hlog = self.hlog.clone();
        let operation_lock = self.operation_lock.clone();

        tokio::spawn(async move {
            let mut interval = interval(Duration::from_secs(30)); // Run every 30 seconds
            interval.set_missed_tick_behavior(MissedTickBehavior::Delay);

            log::info!("Log maintenance task started");

            while running.load(Ordering::Acquire) {
                interval.tick().await;

                if !running.load(Ordering::Acquire) {
                    break;
                }

                // Try to acquire read lock for maintenance
                if let Ok(_lock) = operation_lock.try_read() {
                    // Perform log maintenance operations
                    Self::perform_log_maintenance(&hlog).await;
                }
            }

            log::info!("Log maintenance task stopped");
        })
    }

    /// Perform log maintenance operations
    async fn perform_log_maintenance(hlog: &HybridLog) {
        // Check if we need to advance the read-only address
        let tail_address = hlog.get_tail_address();
        let read_only_address = hlog.get_read_only_address();
        let head_address = hlog.get_head_address();

        // If mutable region is getting large, advance read-only address
        let mutable_region_size = tail_address.saturating_sub(read_only_address);
        const MAX_MUTABLE_REGION_SIZE: u64 = 128 * 1024 * 1024; // 128MB

        if mutable_region_size > MAX_MUTABLE_REGION_SIZE {
            let new_read_only = hlog.shift_read_only_address();
            log::debug!("Advanced read-only address to 0x{new_read_only:x}");

            // Try to flush the newly read-only data
            if let Err(e) = hlog.flush_to_disk(new_read_only).await {
                log::warn!("Failed to flush during maintenance: {e}");
            }
        }

        // Check if we need to advance the head address
        let read_only_region_size = read_only_address.saturating_sub(head_address);
        const MAX_READ_ONLY_REGION_SIZE: u64 = 256 * 1024 * 1024; // 256MB

        if read_only_region_size > MAX_READ_ONLY_REGION_SIZE {
            // Move some data from memory to disk-only
            let new_head = head_address + (read_only_region_size / 2); // Move half
            if let Err(e) = hlog.shift_head_address(new_head) {
                log::warn!("Failed to shift head address during maintenance: {e}");
            } else {
                log::debug!("Advanced head address to 0x{new_head:x}");
            }
        }
    }

    /// Get statistics about background task performance
    pub fn get_stats(&self) -> BackgroundTaskStats {
        BackgroundTaskStats {
            is_running: self.is_running(),
            checkpoint_enabled: self.config.enable_checkpointing,
            gc_enabled: self.config.enable_gc,
            checkpoint_interval_ms: self.config.checkpoint_interval_ms,
            gc_interval_ms: self.config.gc_interval_ms,
            active_task_count: self.task_handles.lock().len(),
        }
    }
}

impl Drop for BackgroundTaskManager {
    fn drop(&mut self) {
        // Stop background tasks when dropping
        let running = self.running.clone();
        let handles = {
            let mut handles = self.task_handles.lock();
            std::mem::take(&mut *handles)
        };

        if running.swap(false, Ordering::AcqRel) {
            // Cancel all tasks
            for handle in handles {
                handle.abort();
            }
        }
    }
}

/// Statistics about background task performance
#[derive(Debug, Clone)]
pub struct BackgroundTaskStats {
    /// Whether background tasks are currently running
    pub is_running: bool,
    /// Whether checkpointing is enabled
    pub checkpoint_enabled: bool,
    /// Whether garbage collection is enabled
    pub gc_enabled: bool,
    /// Checkpoint interval in milliseconds
    pub checkpoint_interval_ms: u64,
    /// GC interval in milliseconds
    pub gc_interval_ms: u64,
    /// Number of active background tasks
    pub active_task_count: usize,
}

