        let stats_after = manager.get_stats();
        assert!(stats_after.is_running);
        assert!(stats_after.checkpoint_enabled);
        assert!(stats_after.gc_enabled);
        assert!(stats_after.active_task_count > 0);

        manager.stop().await.unwrap();
    }

    #[test]
    fn test_background_manager_drop() {
        tokio::runtime::Runtime::new().unwrap().block_on(async {
            let (manager, _temp_dir) = create_test_background_manager().await;

            manager.start().unwrap();
            assert!(manager.is_running());

            // Drop should stop background tasks
            drop(manager);

            // Give some time for cleanup
            tokio::time::sleep(Duration::from_millis(50)).await;
        });
    }
}

//! Checkpoint and recovery implementation for rskv
//!
//! This module implements non-blocking checkpointing inspired by FASTER's design.
//! It provides consistent snapshots of the entire database state without pausing operations.

use std::path::PathBuf;
use std::sync::Arc;
use std::sync::atomic::{AtomicBool, AtomicU64, Ordering};

use serde::{Deserialize, Serialize};
use tokio::fs as async_fs;
use tokio::time::Instant;

use crate::common::{Address, Key, Result, RsKvError};
use crate::hlog::HybridLog;
use crate::index::SharedMemHashIndex;

/// Metadata for a checkpoint containing all necessary information for recovery
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct CheckpointMetadata {
    /// Unique checkpoint ID
    pub checkpoint_id: u64,
    /// Timestamp when checkpoint was initiated
    pub timestamp: u64,
    /// Log addresses at checkpoint time
    pub log_metadata: LogMetadata,
    /// Index snapshot information
    pub index_metadata: IndexMetadata,
    /// Version of the checkpoint format
    pub format_version: u32,
    /// Size of the checkpoint in bytes
    pub total_size: u64,
}

/// Log-specific metadata in a checkpoint
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct LogMetadata {
    /// Begin address of the log
    pub begin_address: Address,
    /// Head address at checkpoint time
    pub head_address: Address,
    /// Read-only address at checkpoint time
    pub read_only_address: Address,
    /// Tail address at checkpoint time
    pub tail_address: Address,
    /// Address up to which data has been flushed
    pub flushed_until_address: Address,
}

/// Index-specific metadata in a checkpoint
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct IndexMetadata {
    /// Number of entries in the index
    pub entry_count: usize,
    /// Total size of keys in bytes
    pub total_key_size: usize,
    /// Size of the index snapshot file
    pub snapshot_file_size: u64,
    /// Hash of the index snapshot for integrity checking
    pub snapshot_hash: u64,
}

/// State machine for checkpoint operations
pub struct CheckpointState {
    /// Unique ID for this checkpoint
    checkpoint_id: AtomicU64,

    /// Whether a checkpoint is currently in progress
    in_progress: AtomicBool,

    /// Directory where checkpoints are stored
    checkpoint_dir: PathBuf,

    /// Reference to the hybrid log
    hlog: Arc<HybridLog>,

    /// Reference to the hash index
    index: SharedMemHashIndex,

    /// Start time of current checkpoint
    start_time: parking_lot::Mutex<Option<Instant>>,
}

impl CheckpointState {
    /// Create a new checkpoint state manager
    pub fn new(
        checkpoint_dir: PathBuf,
        hlog: Arc<HybridLog>,
        index: SharedMemHashIndex,
    ) -> Result<Self> {
        // Ensure checkpoint directory exists
        std::fs::create_dir_all(&checkpoint_dir)?;

        Ok(Self {
            checkpoint_id: AtomicU64::new(1),
            in_progress: AtomicBool::new(false),
            checkpoint_dir,
            hlog,
            index,
            start_time: parking_lot::Mutex::new(None),
        })
    }

    /// Check if a checkpoint is currently in progress
    pub fn is_in_progress(&self) -> bool {
        self.in_progress.load(Ordering::Acquire)
    }

    /// Initiate a new checkpoint operation
    pub async fn initiate_checkpoint(&self) -> Result<CheckpointMetadata> {
        // Check if checkpoint is already in progress
        if self
            .in_progress
            .compare_exchange(false, true, Ordering::AcqRel, Ordering::Acquire)
            .is_err()
        {
            return Err(RsKvError::CheckpointFailed {
                message: "Checkpoint already in progress".to_string(),
            });
        }

        let checkpoint_id = self.checkpoint_id.fetch_add(1, Ordering::AcqRel);
        let start_time = Instant::now();
        *self.start_time.lock() = Some(start_time);

        log::info!("Initiating checkpoint {checkpoint_id}");

        // Phase 1: Capture current log state and shift read-only address
        let tail_address_before = self.hlog.get_tail_address();
        let checkpoint_address = self.hlog.shift_read_only_address();

        log::debug!(
            "Checkpoint {} shifted read-only to address 0x{:x}",
            checkpoint_id,
            checkpoint_address
        );

        // Phase 2: Create log metadata
        let log_metadata = LogMetadata {
            begin_address: self.hlog.get_begin_address(),
            head_address: self.hlog.get_head_address(),
            read_only_address: checkpoint_address,
            tail_address: tail_address_before,
            flushed_until_address: checkpoint_address, // Will be updated after flush
        };

        // Phase 3: Create index snapshot
        let index_snapshot = self.create_index_snapshot(checkpoint_id).await?;
        let index_metadata = IndexMetadata {
            entry_count: index_snapshot.len(),
            total_key_size: index_snapshot.iter().map(|(k, _)| k.len()).sum(),
            snapshot_file_size: 0, // Will be updated after writing
            snapshot_hash: self.calculate_snapshot_hash(&index_snapshot),
        };

        // Phase 4: Flush log data to disk
        self.hlog.flush_to_disk(checkpoint_address).await?;

        // Phase 5: Write checkpoint files
        let metadata = CheckpointMetadata {
            checkpoint_id,
            timestamp: start_time.elapsed().as_millis() as u64,
            log_metadata,
            index_metadata,
            format_version: 1,
            total_size: 0, // Will be calculated
        };

        self.write_checkpoint_files(checkpoint_id, &metadata, index_snapshot)
            .await?;

        log::info!(
            "Checkpoint {} completed in {:?}",
            checkpoint_id,
            start_time.elapsed()
        );

        // Mark checkpoint as complete
        self.in_progress.store(false, Ordering::Release);

        Ok(metadata)
    }

    /// Create a snapshot of the current index state
    async fn create_index_snapshot(&self, checkpoint_id: u64) -> Result<Vec<(Key, Address)>> {
        log::debug!("Creating index snapshot for checkpoint {checkpoint_id}");

        let snapshot = self.index.snapshot();

        log::debug!("Index snapshot created with {} entries", snapshot.len());
        Ok(snapshot)
    }

    /// Calculate hash of index snapshot for integrity checking
    fn calculate_snapshot_hash(&self, snapshot: &[(Key, Address)]) -> u64 {
        use std::collections::hash_map::DefaultHasher;
        use std::hash::{Hash, Hasher};

        let mut hasher = DefaultHasher::new();

        // Sort snapshot by key for deterministic hashing
        let mut sorted_snapshot = snapshot.to_vec();
        sorted_snapshot.sort_by(|a, b| a.0.cmp(&b.0));

        for (key, address) in sorted_snapshot {
            key.hash(&mut hasher);
            address.hash(&mut hasher);
        }

        hasher.finish()
    }

    /// Write checkpoint files to disk
    async fn write_checkpoint_files(
        &self,
        checkpoint_id: u64,
        metadata: &CheckpointMetadata,
        index_snapshot: Vec<(Key, Address)>,
    ) -> Result<()> {
        let checkpoint_prefix = self
            .checkpoint_dir
            .join(format!("checkpoint_{checkpoint_id}"));

        // Write index snapshot
        let index_file_path = format!("{}.index", checkpoint_prefix.to_string_lossy());
        self.write_index_snapshot(&index_file_path, index_snapshot)
            .await?;

        // Write metadata
        let metadata_file_path = format!("{}.meta", checkpoint_prefix.to_string_lossy());
        self.write_metadata(&metadata_file_path, metadata).await?;

        log::info!(
            "Checkpoint {} files written to {}",
            checkpoint_id,
            checkpoint_prefix.to_string_lossy()
        );

        Ok(())
    }

    /// Write index snapshot to file
    async fn write_index_snapshot(
        &self,
        file_path: &str,
        snapshot: Vec<(Key, Address)>,
    ) -> Result<()> {
        let data = bincode::serialize(&snapshot)?;
        async_fs::write(file_path, data).await?;

        log::debug!("Index snapshot written to {file_path}");
        Ok(())
    }

    /// Write checkpoint metadata to file
    async fn write_metadata(&self, file_path: &str, metadata: &CheckpointMetadata) -> Result<()> {
        let data = bincode::serialize(metadata)?;
        async_fs::write(file_path, data).await?;

        log::debug!("Checkpoint metadata written to {file_path}");
        Ok(())
    }

    /// Recover from the latest checkpoint
    pub async fn recover_from_latest_checkpoint(&self) -> Result<Option<CheckpointMetadata>> {
        let latest_checkpoint = self.find_latest_checkpoint().await?;

        if let Some(checkpoint_id) = latest_checkpoint {
            log::info!("Recovering from checkpoint {checkpoint_id}");
            let metadata = self.load_checkpoint(checkpoint_id).await?;
            Ok(Some(metadata))
        } else {
            log::info!("No checkpoint found, starting fresh");
            Ok(None)
        }
    }

    /// Find the latest checkpoint ID
    async fn find_latest_checkpoint(&self) -> Result<Option<u64>> {
        let mut entries = async_fs::read_dir(&self.checkpoint_dir).await?;
        let mut latest_id = None;

        while let Some(entry) = entries.next_entry().await? {
            let file_name = entry.file_name();
            let file_str = file_name.to_string_lossy();

            if file_str.starts_with("checkpoint_")
                && file_str.ends_with(".meta")
                && let Some(id_str) = file_str
                    .strip_prefix("checkpoint_")
                    .and_then(|s| s.strip_suffix(".meta"))
                && let Ok(id) = id_str.parse::<u64>()
            {
                latest_id = Some(latest_id.unwrap_or(0).max(id));
            }
        }

        Ok(latest_id)
    }

    /// Load a specific checkpoint
    async fn load_checkpoint(&self, checkpoint_id: u64) -> Result<CheckpointMetadata> {
        let checkpoint_prefix = self
            .checkpoint_dir
            .join(format!("checkpoint_{checkpoint_id}"));

        // Load metadata
        let metadata_file_path = format!("{}.meta", checkpoint_prefix.to_string_lossy());
        let metadata_data = async_fs::read(&metadata_file_path).await?;
        let metadata: CheckpointMetadata = bincode::deserialize(&metadata_data)?;

        // Load and restore index snapshot
        let index_file_path = format!("{}.index", checkpoint_prefix.to_string_lossy());
        let index_data = async_fs::read(&index_file_path).await?;
        let index_snapshot: Vec<(Key, Address)> = bincode::deserialize(&index_data)?;

        // Verify snapshot integrity
        let calculated_hash = self.calculate_snapshot_hash(&index_snapshot);
        if calculated_hash != metadata.index_metadata.snapshot_hash {
            return Err(RsKvError::CheckpointFailed {
                message: format!(
                    "Index snapshot hash mismatch: expected {}, got {}",
                    metadata.index_metadata.snapshot_hash, calculated_hash
                ),
            });
        }

        // Restore index from snapshot
        self.index.restore_from_snapshot(index_snapshot);

        log::info!("Checkpoint {checkpoint_id} loaded successfully");
        Ok(metadata)
    }

    /// List all available checkpoints
    pub async fn list_checkpoints(&self) -> Result<Vec<u64>> {
        let mut entries = async_fs::read_dir(&self.checkpoint_dir).await?;
        let mut checkpoint_ids = Vec::new();

        while let Some(entry) = entries.next_entry().await? {
            let file_name = entry.file_name();
            let file_str = file_name.to_string_lossy();

            if file_str.starts_with("checkpoint_")
                && file_str.ends_with(".meta")
                && let Some(id_str) = file_str
                    .strip_prefix("checkpoint_")
                    .and_then(|s| s.strip_suffix(".meta"))
                && let Ok(id) = id_str.parse::<u64>()
            {
                checkpoint_ids.push(id);
            }
        }

        checkpoint_ids.sort();
        Ok(checkpoint_ids)
    }

    /// Delete old checkpoints, keeping only the specified number
    pub async fn cleanup_old_checkpoints(&self, keep_count: usize) -> Result<()> {
        let mut checkpoint_ids = self.list_checkpoints().await?;
        checkpoint_ids.sort();

        if checkpoint_ids.len() <= keep_count {
            return Ok(()); // Nothing to cleanup
        }

        let to_delete = &checkpoint_ids[..checkpoint_ids.len() - keep_count];

        for &checkpoint_id in to_delete {
            self.delete_checkpoint(checkpoint_id).await?;
        }

        log::info!("Cleaned up {} old checkpoints", to_delete.len());
        Ok(())
    }

    /// Delete a specific checkpoint
    async fn delete_checkpoint(&self, checkpoint_id: u64) -> Result<()> {
        let checkpoint_prefix = self
            .checkpoint_dir
            .join(format!("checkpoint_{checkpoint_id}"));

        let metadata_file = format!("{}.meta", checkpoint_prefix.to_string_lossy());
        let index_file = format!("{}.index", checkpoint_prefix.to_string_lossy());

        if async_fs::metadata(&metadata_file).await.is_ok() {
            async_fs::remove_file(&metadata_file).await?;
        }

        if async_fs::metadata(&index_file).await.is_ok() {
            async_fs::remove_file(&index_file).await?;
        }

        log::debug!("Deleted checkpoint {checkpoint_id}");
        Ok(())
    }

    /// Get checkpoint statistics
    pub async fn get_checkpoint_stats(&self) -> Result<CheckpointStats> {
        let checkpoint_ids = self.list_checkpoints().await?;
        let total_count = checkpoint_ids.len();

        let mut total_size = 0u64;
        for &checkpoint_id in &checkpoint_ids {
            let checkpoint_prefix = self
                .checkpoint_dir
                .join(format!("checkpoint_{checkpoint_id}"));

            let metadata_file = format!("{}.meta", checkpoint_prefix.to_string_lossy());
            let index_file = format!("{}.index", checkpoint_prefix.to_string_lossy());

            if let Ok(meta) = async_fs::metadata(&metadata_file).await {
                total_size += meta.len();
            }
            if let Ok(meta) = async_fs::metadata(&index_file).await {
                total_size += meta.len();
            }
        }

        Ok(CheckpointStats {
            total_checkpoints: total_count,
            total_size_bytes: total_size,
            latest_checkpoint_id: checkpoint_ids.last().copied(),
            in_progress: self.is_in_progress(),
        })
    }
}

/// Statistics about checkpoints
#[derive(Debug, Clone)]
pub struct CheckpointStats {
    /// Total number of checkpoints
    pub total_checkpoints: usize,
    /// Total size of all checkpoints in bytes
    pub total_size_bytes: u64,
    /// ID of the latest checkpoint
    pub latest_checkpoint_id: Option<u64>,
    /// Whether a checkpoint is currently in progress
    pub in_progress: bool,
}

#[cfg(test)]
mod tests {
    use tempfile::tempdir;

    use super::*;
    use crate::epoch::EpochManager;
    use crate::hlog::FileStorageDevice;
    use crate::index::new_shared_mem_hash_index;

    async fn create_test_checkpoint_state() -> (CheckpointState, tempfile::TempDir) {
        let temp_dir = tempdir().unwrap();
        let checkpoint_dir = temp_dir.path().join("checkpoints");

        let epoch = Arc::new(EpochManager::new());
        let storage = Box::new(FileStorageDevice::new(temp_dir.path().join("test.log")).unwrap());
        let hlog = Arc::new(HybridLog::new(64 * 1024 * 1024, storage, epoch.clone()).unwrap());
        let index = new_shared_mem_hash_index(epoch);

        let checkpoint_state = CheckpointState::new(checkpoint_dir, hlog, index).unwrap();
        (checkpoint_state, temp_dir)
    }

    #[tokio::test]
    async fn test_checkpoint_creation() {
        let (checkpoint_state, _temp_dir) = create_test_checkpoint_state().await;

        // Add some data to index
        checkpoint_state.index.insert(b"key1".to_vec(), 100);
        checkpoint_state.index.insert(b"key2".to_vec(), 200);

        // Create checkpoint
        let metadata = checkpoint_state.initiate_checkpoint().await.unwrap();

        assert_eq!(metadata.checkpoint_id, 1);
        assert_eq!(metadata.index_metadata.entry_count, 2);
        assert!(!checkpoint_state.is_in_progress());
    }

    #[tokio::test]
    async fn test_checkpoint_recovery() {
        let (checkpoint_state, _temp_dir) = create_test_checkpoint_state().await;

        // Add data and create checkpoint
        checkpoint_state.index.insert(b"key1".to_vec(), 100);
        checkpoint_state.index.insert(b"key2".to_vec(), 200);

        let _metadata = checkpoint_state.initiate_checkpoint().await.unwrap();

        // Clear index
        checkpoint_state.index.clear();
        assert_eq!(checkpoint_state.index.len(), 0);

        // Recover from checkpoint
        let recovered_metadata = checkpoint_state
            .recover_from_latest_checkpoint()
            .await
            .unwrap();

        assert!(recovered_metadata.is_some());
        assert_eq!(checkpoint_state.index.len(), 2);
        assert_eq!(checkpoint_state.index.find(&b"key1".to_vec()), Some(100));
        assert_eq!(checkpoint_state.index.find(&b"key2".to_vec()), Some(200));
    }

    #[tokio::test]
    async fn test_checkpoint_cleanup() {
        let (checkpoint_state, _temp_dir) = create_test_checkpoint_state().await;

        // Create multiple checkpoints
        for i in 0..5 {
            checkpoint_state
                .index
                .insert(format!("key{}", i).into_bytes(), i as u64);
            checkpoint_state.initiate_checkpoint().await.unwrap();
        }

        let checkpoints_before = checkpoint_state.list_checkpoints().await.unwrap();
        assert_eq!(checkpoints_before.len(), 5);

        // Cleanup, keeping only 2
        checkpoint_state.cleanup_old_checkpoints(2).await.unwrap();

        let checkpoints_after = checkpoint_state.list_checkpoints().await.unwrap();
        assert_eq!(checkpoints_after.len(), 2);
        assert_eq!(checkpoints_after, vec![4, 5]); // Should keep the latest 2
    }

    #[tokio::test]
    async fn test_checkpoint_stats() {
        let (checkpoint_state, _temp_dir) = create_test_checkpoint_state().await;

        let stats_before = checkpoint_state.get_checkpoint_stats().await.unwrap();
        assert_eq!(stats_before.total_checkpoints, 0);

        // Create a checkpoint
        checkpoint_state.index.insert(b"key1".to_vec(), 100);
        checkpoint_state.initiate_checkpoint().await.unwrap();

        let stats_after = checkpoint_state.get_checkpoint_stats().await.unwrap();
        assert_eq!(stats_after.total_checkpoints, 1);
        assert_eq!(stats_after.latest_checkpoint_id, Some(1));
        assert!(stats_after.total_size_bytes > 0);
    }
}

//! Common types and error definitions for rskv
//!
//! This module contains core data types and error handling used throughout the system.
//! Inspired by FASTER's address.h and common error handling patterns.

use serde::{Deserialize, Serialize};
use thiserror::Error;

/// Synchronization mode for durability vs performance trade-off
#[derive(Debug, Clone, Copy, PartialEq, Eq, Serialize, Deserialize)]
pub enum SyncMode {
    /// No explicit sync - rely on OS page cache (fastest, least durable)
    None,
    /// Sync data to disk periodically (balanced)
    Periodic,
    /// Sync data after every write (slowest, most durable)
    Always,
    /// Sync only metadata, data can be cached (compromise)
    MetadataOnly,
}

/// Address type representing logical addresses in the hybrid log.
/// Follows FASTER's Address design with 48-bit addressing:
/// - 25 bits for offset within page (32MB page size)
/// - 23 bits for page index (supports ~8M pages)
/// - 16 bits reserved for hash table control bits
pub type Address = u64;

/// Key type for the key-value store.
/// Using Vec<u8> for maximum flexibility with different key types.
pub type Key = Vec<u8>;

/// Value type for the key-value store.
/// Using Vec<u8> for maximum flexibility with different value types.
pub type Value = Vec<u8>;

/// Page size constant - 32MB pages like FASTER
pub const PAGE_SIZE: u32 = 32 * 1024 * 1024; // 32MB

/// Address bit layout constants (matching FASTER's design)
pub const ADDRESS_BITS: u64 = 48;
pub const OFFSET_BITS: u64 = 25;
pub const PAGE_BITS: u64 = ADDRESS_BITS - OFFSET_BITS; // 23 bits
pub const MAX_OFFSET: u32 = (1u32 << OFFSET_BITS) - 1;
pub const MAX_PAGE: u32 = (1u32 << PAGE_BITS) - 1;
pub const INVALID_ADDRESS: Address = 1; // Matches FASTER's kInvalidAddress

/// Address utility functions
#[inline]
pub fn get_page(address: Address) -> u32 {
    ((address >> OFFSET_BITS) & ((1u64 << PAGE_BITS) - 1)) as u32
}

#[inline]
pub fn get_offset(address: Address) -> u32 {
    (address & ((1u64 << OFFSET_BITS) - 1)) as u32
}

#[inline]
pub fn make_address(page: u32, offset: u32) -> Address {
    ((page as u64) << OFFSET_BITS) | (offset as u64)
}

/// Error types for rskv operations
#[derive(Error, Debug)]
pub enum RsKvError {
    #[error("IO Error: {0}")]
    Io(#[from] std::io::Error),

    #[error("Serialization Error: {0}")]
    Serialization(#[from] bincode::Error),

    /// Key not found in the store
    #[error("Key not found")]
    KeyNotFound,

    #[error("Address out of bounds: {address}")]
    AddressOutOfBounds { address: Address },

    #[error("Page not found: {page}")]
    PageNotFound { page: u32 },

    #[error("Allocation failed: size {size}")]
    AllocationFailed { size: u32 },

    #[error("Checkpoint operation failed: {message}")]
    CheckpointFailed { message: String },

    #[error("Recovery operation failed: {message}")]
    RecoveryFailed { message: String },

    #[error("Garbage collection failed: {message}")]
    GarbageCollectionFailed { message: String },

    #[error("Configuration error: {message}")]
    Configuration { message: String },

    /// Invalid configuration
    #[error("Invalid configuration: {message}")]
    InvalidConfig { message: String },

    /// Key is too large
    #[error("Key size {size} bytes exceeds maximum allowed size {max_size} bytes")]
    KeyTooLarge { size: usize, max_size: usize },

    /// Value is too large  
    #[error("Value size {size} bytes exceeds maximum allowed size {max_size} bytes")]
    ValueTooLarge { size: usize, max_size: usize },

    /// Storage device error
    #[error("Storage device error: {message}")]
    StorageError { message: String },

    /// Memory mapping error
    #[error("Memory mapping error: {message}")]
    MmapError { message: String },

    /// Data corruption detected
    #[error("Data corruption detected: {message}")]
    Corruption { message: String },

    /// Resource exhausted
    #[error("Resource exhausted: {resource}")]
    ResourceExhausted { resource: String },

    /// Operation timeout
    #[error("Operation timed out after {duration_ms} ms")]
    Timeout { duration_ms: u64 },

    /// Concurrent operation conflict
    #[error("Concurrent operation conflict: {message}")]
    Conflict { message: String },

    #[error("Internal error: {message}")]
    Internal { message: String },
}

impl RsKvError {
    /// Check if this error is recoverable
    pub fn is_recoverable(&self) -> bool {
        matches!(
            self,
            RsKvError::Io(_)
                | RsKvError::Timeout { .. }
                | RsKvError::Conflict { .. }
                | RsKvError::ResourceExhausted { .. }
                | RsKvError::StorageError { .. }
                | RsKvError::MmapError { .. }
        )
    }

    /// Check if this error indicates data corruption
    pub fn is_corruption(&self) -> bool {
        matches!(self, RsKvError::Corruption { .. })
    }

    /// Check if this error is a user input error
    pub fn is_user_error(&self) -> bool {
        matches!(
            self,
            RsKvError::KeyNotFound
                | RsKvError::KeyTooLarge { .. }
                | RsKvError::ValueTooLarge { .. }
                | RsKvError::InvalidConfig { .. }
                | RsKvError::Configuration { .. }
        )
    }

    /// Get error category for logging and metrics
    pub fn category(&self) -> &'static str {
        match self {
            RsKvError::Io(_) => "io",
            RsKvError::Serialization(_) => "serialization",
            RsKvError::AddressOutOfBounds { .. } => "addressing",
            RsKvError::PageNotFound { .. } => "addressing",
            RsKvError::AllocationFailed { .. } => "allocation",
            RsKvError::KeyNotFound => "not_found",
            RsKvError::KeyTooLarge { .. } | RsKvError::ValueTooLarge { .. } => "size_limit",
            RsKvError::CheckpointFailed { .. } => "checkpoint",
            RsKvError::RecoveryFailed { .. } => "recovery",
            RsKvError::GarbageCollectionFailed { .. } => "garbage_collection",
            RsKvError::Configuration { .. } | RsKvError::InvalidConfig { .. } => "configuration",
            RsKvError::StorageError { .. } => "storage",
            RsKvError::MmapError { .. } => "memory_mapping",
            RsKvError::Corruption { .. } => "corruption",
            RsKvError::ResourceExhausted { .. } => "resource_exhausted",
            RsKvError::Timeout { .. } => "timeout",
            RsKvError::Conflict { .. } => "conflict",
            RsKvError::Internal { .. } => "internal",
        }
    }
}

// Error conversion implementations
// Note: memmap2::Error is private, so we convert through std::io::Error

impl From<std::num::TryFromIntError> for RsKvError {
    fn from(err: std::num::TryFromIntError) -> Self {
        RsKvError::Internal {
            message: format!("Integer conversion error: {err}"),
        }
    }
}

/// Result type alias for rskv operations
pub type Result<T> = std::result::Result<T, RsKvError>;

/// Record header information (matches FASTER's RecordInfo)
#[derive(Debug, Clone, Copy, Serialize, Deserialize)]
pub struct RecordInfo {
    /// Previous address in the version chain
    pub previous_address: Address,
    /// Checkpoint version when this record was created
    pub checkpoint_version: u16,
    /// Whether this record is marked as invalid
    pub invalid: bool,
    /// Whether this is a tombstone (deleted) record
    pub tombstone: bool,
    /// Whether this is the final record in a version chain
    pub final_bit: bool,
}

impl RecordInfo {
    pub fn new(
        previous_address: Address,
        checkpoint_version: u16,
        final_bit: bool,
        tombstone: bool,
        invalid: bool,
    ) -> Self {
        Self {
            previous_address,
            checkpoint_version,
            invalid,
            tombstone,
            final_bit,
        }
    }

    pub fn is_null(&self) -> bool {
        self.previous_address == 0 && self.checkpoint_version == 0
    }
}

/// Configuration for rskv instance
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct Config {
    /// Size of the hybrid log in memory (in bytes)
    pub memory_size: u64,
    /// Page size for the hybrid log
    pub page_size: u32,
    /// Directory for storing persistent data
    pub storage_dir: String,
    /// Whether to enable checkpointing
    pub enable_checkpointing: bool,
    /// Checkpoint interval in milliseconds
    pub checkpoint_interval_ms: u64,
    /// Whether to enable garbage collection
    pub enable_gc: bool,
    /// GC interval in milliseconds
    pub gc_interval_ms: u64,
    /// Maximum number of background threads
    pub max_background_threads: usize,
    /// Use memory mapping for storage devices
    pub use_mmap: bool,
    /// Enable read-ahead prefetching  
    pub enable_readahead: bool,
    /// Read-ahead buffer size in bytes
    pub readahead_size: usize,
    /// Enable write batching for better performance
    pub enable_write_batching: bool,
    /// Write batch size in bytes
    pub write_batch_size: usize,
    /// Enable compression for log data
    pub enable_compression: bool,
    /// Sync mode for durability vs performance trade-off
    pub sync_mode: SyncMode,
    /// Pre-allocate log file space
    pub preallocate_log: bool,
    /// Log file preallocation size in bytes
    pub log_prealloc_size: u64,
}

impl Config {
    /// Validate the configuration parameters
    pub fn validate(&self) -> Result<()> {
        // Memory size validation
        if self.memory_size < 1024 * 1024 {
            return Err(RsKvError::InvalidConfig {
                message: "Memory size must be at least 1MB".to_string(),
            });
        }

        if self.memory_size > 64 * 1024 * 1024 * 1024 {
            return Err(RsKvError::InvalidConfig {
                message: "Memory size cannot exceed 64GB".to_string(),
            });
        }

        // Page size validation
        if self.page_size < 4096 {
            return Err(RsKvError::InvalidConfig {
                message: "Page size must be at least 4KB".to_string(),
            });
        }

        if !self.page_size.is_power_of_two() {
            return Err(RsKvError::InvalidConfig {
                message: "Page size must be a power of 2".to_string(),
            });
        }

        if u64::from(self.page_size) > self.memory_size {
            return Err(RsKvError::InvalidConfig {
                message: "Page size cannot be larger than memory size".to_string(),
            });
        }

        // Storage directory validation
        if self.storage_dir.is_empty() {
            return Err(RsKvError::InvalidConfig {
                message: "Storage directory cannot be empty".to_string(),
            });
        }

        // Interval validation
        if self.checkpoint_interval_ms < 100 {
            return Err(RsKvError::InvalidConfig {
                message: "Checkpoint interval must be at least 100ms".to_string(),
            });
        }

        if self.gc_interval_ms < 1000 {
            return Err(RsKvError::InvalidConfig {
                message: "GC interval must be at least 1000ms".to_string(),
            });
        }

        // Thread count validation
        if self.max_background_threads == 0 {
            return Err(RsKvError::InvalidConfig {
                message: "Maximum background threads must be at least 1".to_string(),
            });
        }

        if self.max_background_threads > 32 {
            return Err(RsKvError::InvalidConfig {
                message: "Maximum background threads cannot exceed 32".to_string(),
            });
        }

        // Cross-parameter validation
        if self.checkpoint_interval_ms > self.gc_interval_ms {
            log::warn!(
                "Checkpoint interval ({} ms) is longer than GC interval ({} ms), this might cause \
                 performance issues",
                self.checkpoint_interval_ms,
                self.gc_interval_ms
            );
        }

        Ok(())
    }

    /// Create a configuration with memory size optimization
    pub fn with_memory_size(memory_size: u64) -> Result<Self> {
        let mut config = Self {
            memory_size,
            ..Self::default()
        };

        // Adjust page size based on memory size for optimal performance
        if memory_size >= 8 * 1024 * 1024 * 1024 {
            // 8GB+: Use 64MB pages
            config.page_size = 64 * 1024 * 1024;
        } else if memory_size >= 1024 * 1024 * 1024 {
            // 1GB+: Use 32MB pages (default)
            config.page_size = 32 * 1024 * 1024;
        } else if memory_size >= 256 * 1024 * 1024 {
            // 256MB+: Use 16MB pages
            config.page_size = 16 * 1024 * 1024;
        } else {
            // <256MB: Use 8MB pages
            config.page_size = 8 * 1024 * 1024;
        }

        config.validate()?;
        Ok(config)
    }

    /// Create a configuration optimized for high-performance scenarios
    pub fn high_performance() -> Result<Self> {
        let config = Self {
            memory_size: 4 * 1024 * 1024 * 1024, // 4GB
            page_size: 64 * 1024 * 1024,         // 64MB pages
            checkpoint_interval_ms: 30000,       // 30 seconds
            gc_interval_ms: 60000,               // 1 minute
            max_background_threads: 8,
            ..Self::default()
        };

        config.validate()?;
        Ok(config)
    }

    /// Create a configuration optimized for low-memory scenarios
    pub fn low_memory() -> Result<Self> {
        let config = Self {
            memory_size: 64 * 1024 * 1024, // 64MB
            page_size: 4 * 1024 * 1024,    // 4MB pages
            checkpoint_interval_ms: 2000,  // 2 seconds
            gc_interval_ms: 5000,          // 5 seconds
            max_background_threads: 2,
            ..Self::default()
        };

        config.validate()?;
        Ok(config)
    }
}

impl Default for Config {
    fn default() -> Self {
        Self {
            memory_size: 1024 * 1024 * 1024, // 1GB
            page_size: PAGE_SIZE,
            storage_dir: "./rskv_data".to_string(),
            enable_checkpointing: true,
            checkpoint_interval_ms: 5000, // 5 seconds
            enable_gc: true,
            gc_interval_ms: 10000, // 10 seconds
            max_background_threads: 4,
            use_mmap: true, // Enable mmap by default for better performance
            enable_readahead: true,
            readahead_size: 1024 * 1024, // 1MB
            enable_write_batching: true,
            write_batch_size: 64 * 1024, // 64KB
            enable_compression: false,   // Disabled by default for simplicity
            sync_mode: SyncMode::Periodic,
            preallocate_log: true,
            log_prealloc_size: 100 * 1024 * 1024, // 100MB
        }
    }
}

