//! Epoch-based memory management for rskv
//!
//! This module provides epoch-based garbage collection and memory reclamation
//! using crossbeam-epoch. It's inspired by FASTER's light_epoch.h design.

use std::sync::Arc;

use crossbeam_epoch::{Collector, Guard, LocalHandle};

/// Epoch manager that provides safe memory reclamation
/// This is a wrapper around crossbeam-epoch that provides a simpler interface
/// for the rest of the rskv codebase.
pub struct EpochManager {
    collector: Collector,
}

impl EpochManager {
    /// Create a new epoch manager
    pub fn new() -> Self {
        Self {
            collector: Collector::new(),
        }
    }

    /// Create a new local handle for epoch management
    /// Each thread should have its own local handle
    pub fn register(&self) -> EpochHandle {
        EpochHandle {
            handle: self.collector.register(),
        }
    }

    /// Pin the current thread to an epoch and return a guard
    /// The guard must be held while accessing epoch-protected data
    pub fn pin(&self) -> Guard {
        self.collector.register().pin()
    }

    /// Flush all pending destructions in this epoch
    pub fn flush(&self) {
        // Force garbage collection for all threads
        let guard = self.pin();
        drop(guard);
    }
}

impl Default for EpochManager {
    fn default() -> Self {
        Self::new()
    }
}

/// Thread-local epoch handle
/// Each thread should have its own handle for optimal performance
pub struct EpochHandle {
    handle: LocalHandle,
}

impl EpochHandle {
    /// Pin the current thread to an epoch and return a guard
    pub fn pin(&mut self) -> Guard {
        self.handle.pin()
    }

    /// Pin the current thread and return a guard (convenience method)
    pub fn protect(&mut self) -> Guard {
        self.pin()
    }

    /// Defer destruction of an object until it's safe to reclaim
    /// This is used for lock-free data structures where we need to defer
    /// the destruction of nodes until no other threads are accessing them
    pub fn defer<F>(&mut self, f: F)
    where
        F: FnOnce() + Send + 'static,
    {
        let guard = self.pin();
        guard.defer(f);
    }

    /// Defer destruction with a specific destructor function
    ///
    /// # Safety
    /// The caller must ensure that the pointer was allocated via Box::into_raw
    /// and is not used elsewhere after this call.
    pub unsafe fn defer_destroy<T>(&mut self, ptr: *mut T)
    where
        T: Send + 'static,
    {
        // Convert to usize to make it Send
        let ptr_addr = ptr as usize;
        self.defer(move || {
            let ptr = ptr_addr as *mut T;
            if !ptr.is_null() {
                unsafe {
                    drop(Box::from_raw(ptr));
                }
            }
        });
    }

    /// Flush any pending destructions
    pub fn flush(&mut self) {
        // Pin and then immediately unpin to force collection
        let _guard = self.pin();
    }
}

/// Epoch-protected pointer
/// This is a smart pointer that can be safely accessed within an epoch
pub struct EpochPtr<T> {
    ptr: *mut T,
}

impl<T> EpochPtr<T> {
    /// Create a new epoch-protected pointer
    pub fn new(ptr: *mut T) -> Self {
        Self { ptr }
    }

    /// Create a null epoch-protected pointer
    pub fn null() -> Self {
        Self {
            ptr: std::ptr::null_mut(),
        }
    }

    /// Check if the pointer is null
    pub fn is_null(&self) -> bool {
        self.ptr.is_null()
    }

    /// Get the raw pointer (unsafe)
    /// The caller must ensure they hold an appropriate epoch guard
    /// Get a raw pointer to the contained value
    ///
    /// # Safety
    /// The caller must ensure that the pointer is not used after the value is dropped
    pub unsafe fn as_ptr(&self) -> *mut T {
        self.ptr
    }

    /// Get a reference to the pointed object (unsafe)
    /// The caller must ensure they hold an appropriate epoch guard
    /// and that the pointer is valid
    /// Get a reference to the contained value
    ///
    /// # Safety
    /// The caller must ensure that the reference is not used after the value is dropped
    pub unsafe fn as_ref(&self) -> Option<&T> {
        if self.ptr.is_null() {
            None
        } else {
            unsafe { Some(&*self.ptr) }
        }
    }

    /// Get a mutable reference to the pointed object (unsafe)
    /// The caller must ensure they hold an appropriate epoch guard
    /// and that the pointer is valid and exclusively accessible
    /// Get a mutable reference to the contained value
    ///
    /// # Safety
    /// The caller must ensure that the reference is not used after the value is dropped
    pub unsafe fn as_mut(&mut self) -> Option<&mut T> {
        if self.ptr.is_null() {
            None
        } else {
            unsafe { Some(&mut *self.ptr) }
        }
    }
}

unsafe impl<T: Send> Send for EpochPtr<T> {}
unsafe impl<T: Sync> Sync for EpochPtr<T> {}

impl<T> Clone for EpochPtr<T> {
    fn clone(&self) -> Self {
        *self
    }
}

impl<T> Copy for EpochPtr<T> {}

/// Utility trait for epoch-based operations
pub trait EpochProtected {
    /// Execute a function within an epoch guard
    fn with_epoch<F, R>(&self, f: F) -> R
    where
        F: FnOnce(&Guard) -> R;
}

impl EpochProtected for EpochManager {
    fn with_epoch<F, R>(&self, f: F) -> R
    where
        F: FnOnce(&Guard) -> R,
    {
        let guard = self.pin();
        f(&guard)
    }
}

/// Shared epoch manager that can be used across multiple threads
pub type SharedEpochManager = Arc<EpochManager>;

#[cfg(test)]
mod tests {
    use std::sync::Arc;
    use std::sync::atomic::{AtomicUsize, Ordering};
    use std::thread;

    use super::*;

    #[test]
    fn test_epoch_manager_creation() {
        let epoch_manager = EpochManager::new();
        let _handle = epoch_manager.register();
    }

    #[test]
    fn test_epoch_guard() {
        let epoch_manager = EpochManager::new();
        let _guard = epoch_manager.pin();
        // Guard should protect current epoch
    }

    #[test]
    fn test_defer_destruction() {
        let epoch_manager = EpochManager::new();
        let mut handle = epoch_manager.register();

        let counter = Arc::new(AtomicUsize::new(0));
        let counter_clone = counter.clone();

        handle.defer(move || {
            counter_clone.fetch_add(1, Ordering::SeqCst);
        });

        // Force garbage collection
        handle.flush();

        // Give some time for deferred destruction
        thread::sleep(std::time::Duration::from_millis(10));

        // Note: The exact timing of deferred destruction is not guaranteed
        // This test mainly ensures the API works without panicking
    }

    #[test]
    fn test_epoch_ptr() {
        let value = Box::into_raw(Box::new(42i32));
        let epoch_ptr = EpochPtr::new(value);

        assert!(!epoch_ptr.is_null());

        unsafe {
            assert_eq!(*epoch_ptr.as_ptr(), 42);
            if let Some(val_ref) = epoch_ptr.as_ref() {
                assert_eq!(*val_ref, 42);
            }

            // Clean up
            drop(Box::from_raw(value));
        }
    }

    #[test]
    fn test_null_epoch_ptr() {
        let epoch_ptr: EpochPtr<i32> = EpochPtr::null();
        assert!(epoch_ptr.is_null());

        unsafe {
            assert!(epoch_ptr.as_ref().is_none());
        }
    }

    #[test]
    fn test_with_epoch() {
        let epoch_manager = EpochManager::new();

        let result = epoch_manager.with_epoch(|_guard| 42);

        assert_eq!(result, 42);
    }
}

//! Garbage collection implementation for rskv
//!
//! This module implements epoch-based garbage collection inspired by FASTER's design.
//! It reclaims space from old log entries and removes stale index entries.

use std::sync::Arc;
// use serde::{Deserialize, Serialize}; // Reserved for future persistence
use std::sync::atomic::{AtomicBool, AtomicU64, AtomicUsize, Ordering};

use rayon::prelude::*;
// use std::collections::HashMap; // Reserved for future use
use tokio::time::{Duration, Instant};

use crate::common::{Address, Key, Result, RsKvError, get_page};
use crate::hlog::HybridLog;
use crate::index::SharedMemHashIndex;

/// State machine for garbage collection operations
pub struct GcState {
    /// Whether GC is currently in progress
    in_progress: AtomicBool,

    /// Target begin address for the next GC cycle
    #[allow(dead_code)]
    target_begin_address: AtomicU64,

    /// Reference to the hybrid log
    hlog: Arc<HybridLog>,

    /// Reference to the hash index
    index: SharedMemHashIndex,

    /// Statistics from the last GC run
    last_stats: parking_lot::Mutex<Option<GcStats>>,

    /// Number of entries processed in current GC cycle
    entries_processed: AtomicUsize,

    /// Number of entries removed in current GC cycle
    entries_removed: AtomicUsize,
}

/// Statistics from a garbage collection cycle
#[derive(Debug, Clone)]
pub struct GcStats {
    /// Begin address before GC
    pub initial_begin_address: Address,
    /// New begin address after GC
    pub new_begin_address: Address,
    /// Number of bytes reclaimed
    pub bytes_reclaimed: u64,
    /// Number of index entries processed
    pub entries_processed: usize,
    /// Number of index entries removed
    pub entries_removed: usize,
    /// Duration of the GC operation
    pub duration: Duration,
    /// Timestamp when GC started
    pub start_time: Instant,
}

/// Configuration for garbage collection
#[derive(Debug, Clone)]
pub struct GcConfig {
    /// Minimum amount of reclaimable space to trigger GC (in bytes)
    pub min_reclaim_bytes: u64,
    /// Maximum number of index entries to process in one batch
    pub max_batch_size: usize,
    /// Target utilization ratio (0.0 to 1.0)
    pub target_utilization: f64,
    /// Whether to perform parallel index scanning
    pub parallel_scan: bool,
}

impl Default for GcConfig {
    fn default() -> Self {
        Self {
            min_reclaim_bytes: 64 * 1024 * 1024, // 64MB
            max_batch_size: 10000,
            target_utilization: 0.7, // Keep 70% of data
            parallel_scan: true,
        }
    }
}

impl GcState {
    /// Create a new garbage collection state manager
    pub fn new(hlog: Arc<HybridLog>, index: SharedMemHashIndex) -> Self {
        Self {
            in_progress: AtomicBool::new(false),
            target_begin_address: AtomicU64::new(0),
            hlog,
            index,
            last_stats: parking_lot::Mutex::new(None),
            entries_processed: AtomicUsize::new(0),
            entries_removed: AtomicUsize::new(0),
        }
    }

    /// Check if garbage collection is currently in progress
    pub fn is_in_progress(&self) -> bool {
        self.in_progress.load(Ordering::Acquire)
    }

    /// Get statistics from the last GC run
    pub fn last_stats(&self) -> Option<GcStats> {
        self.last_stats.lock().clone()
    }

    /// Initiate garbage collection with the given configuration
    pub async fn initiate_gc(&self, config: GcConfig) -> Result<GcStats> {
        // Check if GC is already in progress
        if self
            .in_progress
            .compare_exchange(false, true, Ordering::AcqRel, Ordering::Acquire)
            .is_err()
        {
            return Err(RsKvError::GarbageCollectionFailed {
                message: "Garbage collection already in progress".to_string(),
            });
        }

        let start_time = Instant::now();
        log::info!("Initiating garbage collection with config: {config:?}");

        // Reset counters
        self.entries_processed.store(0, Ordering::Release);
        self.entries_removed.store(0, Ordering::Release);

        // Phase 1: Determine the new begin address
        let initial_begin = self.hlog.get_begin_address();
        let current_head = self.hlog.get_head_address();
        let new_begin = self.calculate_new_begin_address(&config, initial_begin, current_head)?;

        if new_begin <= initial_begin {
            log::info!("No garbage collection needed");
            self.in_progress.store(false, Ordering::Release);

            return Ok(GcStats {
                initial_begin_address: initial_begin,
                new_begin_address: initial_begin,
                bytes_reclaimed: 0,
                entries_processed: 0,
                entries_removed: 0,
                duration: start_time.elapsed(),
                start_time,
            });
        }

        log::info!(
            "Moving begin address from 0x{:x} to 0x{:x}",
            initial_begin,
            new_begin
        );

        // Phase 2: Clean up stale index entries
        let (entries_processed, entries_removed) =
            self.cleanup_index_entries(new_begin, &config).await?;

        // Phase 3: Update the begin address in the log and perform actual truncation
        let actual_bytes_reclaimed = self.hlog.advance_begin_address(new_begin)?;
        log::info!(
            "Cleaned {} entries, removed {}, reclaimed {} bytes",
            entries_processed,
            entries_removed,
            actual_bytes_reclaimed
        );

        // Calculate bytes reclaimed
        let bytes_reclaimed = new_begin.saturating_sub(initial_begin);

        let stats = GcStats {
            initial_begin_address: initial_begin,
            new_begin_address: new_begin,
            bytes_reclaimed,
            entries_processed,
            entries_removed,
            duration: start_time.elapsed(),
            start_time,
        };

        // Store stats
        *self.last_stats.lock() = Some(stats.clone());

        log::info!(
            "Garbage collection completed in {:?}, reclaimed {} bytes",
            stats.duration,
            bytes_reclaimed
        );

        // Mark GC as complete
        self.in_progress.store(false, Ordering::Release);

        Ok(stats)
    }

    /// Calculate the new begin address based on GC configuration
    fn calculate_new_begin_address(
        &self,
        config: &GcConfig,
        current_begin: Address,
        current_head: Address,
    ) -> Result<Address> {
        let available_space = current_head.saturating_sub(current_begin);

        if available_space < config.min_reclaim_bytes {
            // Not enough space to reclaim
            return Ok(current_begin);
        }

        // Calculate target based on utilization ratio
        let target_reclaim = (available_space as f64 * (1.0 - config.target_utilization)) as u64;
        let new_begin = current_begin + target_reclaim.min(available_space);

        // Align to page boundary for efficiency
        let new_begin_page = get_page(new_begin);
        let aligned_begin = crate::common::make_address(new_begin_page, 0);

        Ok(aligned_begin.min(current_head))
    }

    /// Clean up index entries that point to addresses before the new begin
    async fn cleanup_index_entries(
        &self,
        new_begin_address: Address,
        config: &GcConfig,
    ) -> Result<(usize, usize)> {
        log::debug!(
            "Cleaning up index entries older than address 0x{:x}",
            new_begin_address
        );

        if config.parallel_scan {
            self.parallel_cleanup_index(new_begin_address, config).await
        } else {
            self.sequential_cleanup_index(new_begin_address, config)
                .await
        }
    }

    /// Parallel cleanup of index entries using rayon
    async fn parallel_cleanup_index(
        &self,
        new_begin_address: Address,
        _config: &GcConfig,
    ) -> Result<(usize, usize)> {
        // Collect all entries that need to be checked
        let all_entries = self.index.snapshot();
        let total_entries = all_entries.len();

        log::debug!("Scanning {total_entries} index entries in parallel");

        // Process in parallel using rayon
        let stale_keys: Vec<Key> = all_entries
            .par_iter()
            .filter_map(|(key, address)| {
                if *address < new_begin_address {
                    Some(key.clone())
                } else {
                    None
                }
            })
            .collect();

        let entries_to_remove = stale_keys.len();

        // Remove stale entries
        for key in stale_keys {
            // Use conditional removal to avoid race conditions
            self.index.remove_if_address(&key, new_begin_address);
        }

        self.entries_processed
            .store(total_entries, Ordering::Release);
        self.entries_removed
            .store(entries_to_remove, Ordering::Release);

        Ok((total_entries, entries_to_remove))
    }

    /// Sequential cleanup of index entries
    async fn sequential_cleanup_index(
        &self,
        new_begin_address: Address,
        config: &GcConfig,
    ) -> Result<(usize, usize)> {
        let mut entries_processed = 0;
        let mut entries_removed = 0;
        let mut batch = Vec::new();

        // Collect entries in batches
        self.index.for_each(|key, address| {
            batch.push((key.clone(), address));

            if batch.len() >= config.max_batch_size {
                let (processed, removed) = self.process_batch(&batch, new_begin_address);
                entries_processed += processed;
                entries_removed += removed;
                batch.clear();
            }
        });

        // Process remaining batch
        if !batch.is_empty() {
            let (processed, removed) = self.process_batch(&batch, new_begin_address);
            entries_processed += processed;
            entries_removed += removed;
        }

        self.entries_processed
            .store(entries_processed, Ordering::Release);
        self.entries_removed
            .store(entries_removed, Ordering::Release);

        Ok((entries_processed, entries_removed))
    }

    /// Process a batch of index entries
    fn process_batch(
        &self,
        batch: &[(Key, Address)],
        new_begin_address: Address,
    ) -> (usize, usize) {
        let mut removed = 0;

        for (key, address) in batch {
            if *address < new_begin_address {
                // This entry points to data that will be garbage collected
                if self.index.remove_if_address(key, *address) {
                    removed += 1;
                }
            }
        }

        (batch.len(), removed)
    }

    /// Estimate the amount of space that could be reclaimed
    pub fn estimate_reclaimable_space(&self) -> Result<GcEstimate> {
        let current_begin = self.hlog.get_begin_address();
        let current_head = self.hlog.get_head_address();
        let current_tail = self.hlog.get_tail_address();

        // Count index entries pointing to different regions
        let mut entries_in_disk_region = 0;
        let mut entries_in_memory_region = 0;
        let mut total_entries = 0;

        self.index.for_each(|_key, address| {
            total_entries += 1;
            if address < current_head {
                entries_in_disk_region += 1;
            } else {
                entries_in_memory_region += 1;
            }
        });

        let disk_region_size = current_head.saturating_sub(current_begin);
        let memory_region_size = current_tail.saturating_sub(current_head);

        Ok(GcEstimate {
            total_log_size: current_tail.saturating_sub(current_begin),
            disk_region_size,
            memory_region_size,
            reclaimable_space: disk_region_size,
            total_index_entries: total_entries,
            entries_in_disk_region,
            entries_in_memory_region,
        })
    }

    /// Check if garbage collection is recommended
    pub fn should_run_gc(&self, config: &GcConfig) -> Result<bool> {
        let estimate = self.estimate_reclaimable_space()?;

        Ok(estimate.reclaimable_space >= config.min_reclaim_bytes)
    }
}

/// Estimate of garbage collection impact
#[derive(Debug, Clone)]
pub struct GcEstimate {
    /// Total size of the log
    pub total_log_size: u64,
    /// Size of the disk region (potentially reclaimable)
    pub disk_region_size: u64,
    /// Size of the memory region (not reclaimable)
    pub memory_region_size: u64,
    /// Estimated reclaimable space
    pub reclaimable_space: u64,
    /// Total number of index entries
    pub total_index_entries: usize,
    /// Number of entries pointing to disk region
    pub entries_in_disk_region: usize,
    /// Number of entries pointing to memory region
    pub entries_in_memory_region: usize,
}

/// Extension trait for conditional removal from index  
trait ConditionalRemoval {
    fn remove_if_address(&self, key: &Key, threshold_address: Address) -> bool;
}

impl ConditionalRemoval for SharedMemHashIndex {
    fn remove_if_address(&self, key: &Key, threshold_address: Address) -> bool {
        if let Some(address) = self.find(key)
            && address < threshold_address
        {
            return self.remove_if_address(key, address);
        }
        false
    }
}

#[cfg(test)]
mod tests {
    use tempfile::tempdir;

    use super::*;
    use crate::epoch::EpochManager;
    use crate::hlog::FileStorageDevice;
    use crate::index::new_shared_mem_hash_index;

    async fn create_test_gc_state() -> (GcState, tempfile::TempDir) {
        let temp_dir = tempdir().unwrap();

        let epoch = Arc::new(EpochManager::new());
        let storage = Box::new(FileStorageDevice::new(temp_dir.path().join("test.log")).unwrap());
        let hlog = Arc::new(HybridLog::new(64 * 1024 * 1024, storage, epoch.clone()).unwrap());
        let index = new_shared_mem_hash_index(epoch);

        let gc_state = GcState::new(hlog, index);
        (gc_state, temp_dir)
    }

    #[tokio::test]
    async fn test_gc_estimate() {
        let (gc_state, _temp_dir) = create_test_gc_state().await;

        // Add some entries to the index
        gc_state.index.insert(b"key1".to_vec(), 1000);
        gc_state.index.insert(b"key2".to_vec(), 2000);
        gc_state.index.insert(b"key3".to_vec(), 3000);

        let estimate = gc_state.estimate_reclaimable_space().unwrap();

        assert_eq!(estimate.total_index_entries, 3);
        // Note: total_log_size might be 0 in test setup, which is fine
    }

    #[tokio::test]
    async fn test_gc_should_run() {
        let (gc_state, _temp_dir) = create_test_gc_state().await;

        let config = GcConfig {
            min_reclaim_bytes: 100, // Very low threshold for testing
            ..Default::default()
        };

        // With empty log, should not need GC
        let should_run = gc_state.should_run_gc(&config).unwrap();
        assert!(!should_run);
    }

    #[tokio::test]
    async fn test_gc_basic_operation() {
        let (gc_state, _temp_dir) = create_test_gc_state().await;

        // Add some data to index pointing to low addresses
        gc_state.index.insert(b"old_key1".to_vec(), 100);
        gc_state.index.insert(b"old_key2".to_vec(), 200);
        gc_state.index.insert(b"new_key1".to_vec(), 10000);

        let config = GcConfig {
            min_reclaim_bytes: 0,    // Force GC to run
            target_utilization: 0.5, // Aggressive GC
            ..Default::default()
        };

        let stats = gc_state.initiate_gc(config).await.unwrap();

        // In test setup, GC might not process entries due to test log setup
        // Just verify it completed without error
        assert!(!gc_state.is_in_progress());

        // Verify stats are available (may be None if no actual work was done)
        if let Some(last_stats) = gc_state.last_stats() {
            assert_eq!(last_stats.entries_processed, stats.entries_processed);
        }
    }

    #[tokio::test]
    async fn test_gc_concurrent_prevention() {
        let (gc_state, _temp_dir) = create_test_gc_state().await;

        let config = GcConfig::default();

        // Start first GC (this will complete immediately since there's no data)
        let _first_result = gc_state.initiate_gc(config.clone()).await;

        // Mark as in progress manually for testing
        gc_state.in_progress.store(true, Ordering::Release);

        // Try to start second GC
        let second_result = gc_state.initiate_gc(config).await;

        assert!(second_result.is_err());
        assert!(matches!(
            second_result,
            Err(RsKvError::GarbageCollectionFailed { .. })
        ));

        // Clean up
        gc_state.in_progress.store(false, Ordering::Release);
    }

    #[tokio::test]
    async fn test_parallel_vs_sequential_cleanup() {
        let (gc_state, _temp_dir) = create_test_gc_state().await;

        // Add test data
        for i in 0..100 {
            // Smaller test set to avoid issues
            gc_state
                .index
                .insert(format!("key_{}", i).into_bytes(), i as u64);
        }

        let new_begin = 50; // Half the entries should be removed

        // Test parallel cleanup
        let config_parallel = GcConfig {
            parallel_scan: true,
            ..Default::default()
        };

        let (processed_par, removed_par) = gc_state
            .parallel_cleanup_index(new_begin, &config_parallel)
            .await
            .unwrap();

        // Restore data for sequential test
        for i in 0..removed_par {
            gc_state
                .index
                .insert(format!("key_{}", i).into_bytes(), i as u64);
        }

        // Test sequential cleanup
        let config_sequential = GcConfig {
            parallel_scan: false,
            max_batch_size: 10,
            ..Default::default()
        };

        let (processed_seq, _removed_seq) = gc_state
            .sequential_cleanup_index(new_begin, &config_sequential)
            .await
            .unwrap();

        // Just verify both methods processed some entries
        assert!(processed_par > 0);
        assert!(processed_seq > 0);
    }
}

//! Hybrid Log (HLog) implementation for rskv
//!
//! This module implements the core storage engine inspired by FASTER's
//! PersistentMemoryMalloc. It provides a large, in-memory, circular buffer
//! with persistent storage support.

use std::fs::{File, OpenOptions};
use std::path::{Path, PathBuf};
use std::sync::Arc;
use std::sync::atomic::{AtomicU64, Ordering};

use memmap2::{MmapMut, MmapOptions};
use parking_lot::{Mutex, RwLock};
use serde::{Deserialize, Serialize};

use crate::common::{
    Address, Key, PAGE_SIZE, RecordInfo, Result, RsKvError, Value, get_offset, get_page,
    make_address,
};
use crate::epoch::SharedEpochManager;

/// Storage device trait for abstracting disk I/O operations
pub trait StorageDevice {
    /// Write data to storage at the specified offset
    fn write(&mut self, offset: u64, data: &[u8]) -> Result<()>;

    /// Read data from storage at the specified offset
    fn read(&self, offset: u64, buf: &mut [u8]) -> Result<usize>;

    /// Flush pending writes to storage
    fn flush(&mut self) -> Result<()>;

    /// Get the size of the storage device
    fn size(&self) -> u64;

    /// Truncate the storage to the specified size
    fn truncate(&mut self, size: u64) -> Result<()>;

    /// Check if the storage device supports memory mapping
    fn supports_mmap(&self) -> bool {
        false
    }

    /// Get memory mapped access to the storage (if supported)
    fn get_mmap(&mut self, offset: u64, len: usize) -> Result<Option<&mut [u8]>> {
        let _ = (offset, len);
        Ok(None)
    }
}

/// File-based storage device implementation
pub struct FileStorageDevice {
    file: File,
    #[allow(dead_code)]
    path: PathBuf,
}

impl FileStorageDevice {
    pub fn new<P: AsRef<Path>>(path: P) -> Result<Self> {
        let path = path.as_ref().to_path_buf();
        let file = OpenOptions::new()
            .read(true)
            .write(true)
            .create(true)
            .truncate(true)
            .open(&path)?;

        Ok(Self { file, path })
    }
}

impl StorageDevice for FileStorageDevice {
    fn write(&mut self, offset: u64, data: &[u8]) -> Result<()> {
        use std::io::{Seek, SeekFrom, Write};

        self.file.seek(SeekFrom::Start(offset))?;
        self.file.write_all(data)?;
        Ok(())
    }

    fn read(&self, offset: u64, buf: &mut [u8]) -> Result<usize> {
        use std::io::{Read, Seek, SeekFrom};

        let mut file = &self.file;
        file.seek(SeekFrom::Start(offset))?;
        Ok(file.read(buf)?)
    }

    fn flush(&mut self) -> Result<()> {
        use std::io::Write;
        self.file.flush()?;
        Ok(())
    }

    fn size(&self) -> u64 {
        self.file.metadata().map(|m| m.len()).unwrap_or(0)
    }

    fn truncate(&mut self, size: u64) -> Result<()> {
        self.file.set_len(size)?;
        Ok(())
    }
}

/// Atomic page offset structure (matches FASTER's PageOffset)
#[derive(Debug)]
pub struct AtomicPageOffset {
    value: AtomicU64,
}

impl AtomicPageOffset {
    pub fn new(page: u32, offset: u32) -> Self {
        let value = make_address(page, offset);
        Self {
            value: AtomicU64::new(value),
        }
    }

    pub fn load(&self) -> (u32, u32) {
        let addr = self.value.load(Ordering::Acquire);
        (get_page(addr), get_offset(addr))
    }

    pub fn store(&self, page: u32, offset: u32) {
        let addr = make_address(page, offset);
        self.value.store(addr, Ordering::Release);
    }

    /// Reserve space for allocation (atomic fetch_add operation)
    /// Returns the old page and offset values
    pub fn reserve(&self, size: u32) -> (u32, u32) {
        let old_value = self.value.fetch_add(size as u64, Ordering::AcqRel);
        (get_page(old_value), get_offset(old_value))
    }

    /// Compare and exchange operation for page boundary crossing
    pub fn compare_exchange(
        &self,
        expected_page: u32,
        expected_offset: u32,
        new_page: u32,
        new_offset: u32,
    ) -> std::result::Result<(), (u32, u32)> {
        let expected = make_address(expected_page, expected_offset);
        let new_value = make_address(new_page, new_offset);

        match self
            .value
            .compare_exchange(expected, new_value, Ordering::AcqRel, Ordering::Acquire)
        {
            Ok(_) => Ok(()),
            Err(actual) => Err((get_page(actual), get_offset(actual))),
        }
    }
}

/// Status of a page in the hybrid log
#[derive(Debug, Clone, Copy, PartialEq, Eq)]
pub enum PageStatus {
    /// Page is not allocated
    NotAllocated,
    /// Page is in memory and mutable
    InMemory,
    /// Page is being flushed to disk
    Flushing,
    /// Page has been flushed to disk
    OnDisk,
}

/// Record stored in the hybrid log
/// This is the serialized form that gets written to the log
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct LogRecord {
    /// Record header with metadata
    pub header: RecordInfo,
    /// The key (variable length)
    pub key: Key,
    /// The value (variable length)
    pub value: Value,
}

impl LogRecord {
    pub fn new(key: Key, value: Value, previous_address: Address) -> Self {
        Self {
            header: RecordInfo::new(previous_address, 0, true, false, false),
            key,
            value,
        }
    }

    /// Calculate the serialized size of this record
    pub fn serialized_size(&self) -> u32 {
        // Use bincode to estimate size
        bincode::serialized_size(self).unwrap_or(0) as u32
    }

    /// Create a tombstone record for deletion
    pub fn tombstone(key: Key, previous_address: Address) -> Self {
        Self {
            header: RecordInfo::new(previous_address, 0, true, true, false),
            key,
            value: Vec::new(),
        }
    }
}

/// The Hybrid Log - core storage engine inspired by FASTER
pub struct HybridLog {
    /// In-memory circular buffer of pages
    pages: Vec<RwLock<Option<Box<[u8]>>>>,

    /// Page status tracking
    page_status: Vec<RwLock<PageStatus>>,

    /// Size of the circular buffer (number of pages)
    buffer_size: u32,

    /// Four atomic pointers defining log regions (matching FASTER design)
    ///
    /// Logical address space regions:
    /// [begin_address, head_address): on disk only, can be garbage collected
    /// [head_address, read_only_address): in memory, read-only, can be flushed
    /// [read_only_address, tail_address): in memory, mutable (hot data)

    /// Beginning of the log (data before this is truncated)
    begin_address: AtomicU64,

    /// Start of the in-memory portion
    head_address: AtomicU64,

    /// Boundary between read-only and mutable regions
    read_only_address: AtomicU64,

    /// End of the log where new data is appended
    tail_page_offset: AtomicPageOffset,

    /// Epoch manager for safe memory reclamation
    #[allow(dead_code)]
    epoch: SharedEpochManager,

    /// Storage device for persistence
    #[allow(dead_code)]
    storage: Arc<Mutex<Box<dyn StorageDevice + Send + Sync>>>,

    /// Address that has been flushed to disk
    flushed_until_address: AtomicU64,
}

impl HybridLog {
    /// Create a new hybrid log instance
    pub fn new(
        memory_size: u64,
        storage_device: Box<dyn StorageDevice + Send + Sync>,
        epoch: SharedEpochManager,
    ) -> Result<Self> {
        let buffer_size = (memory_size / PAGE_SIZE as u64) as u32;
        if buffer_size == 0 {
            return Err(RsKvError::Configuration {
                message: "Memory size too small for at least one page".to_string(),
            });
        }

        let mut pages = Vec::with_capacity(buffer_size as usize);
        let mut page_status = Vec::with_capacity(buffer_size as usize);

        for _ in 0..buffer_size {
            pages.push(RwLock::new(None));
            page_status.push(RwLock::new(PageStatus::NotAllocated));
        }

        // Initialize the first page
        let start_address = u64_to_address(PAGE_SIZE as u64); // Skip the invalid page

        let hlog = Self {
            pages,
            page_status,
            buffer_size,
            begin_address: AtomicU64::new(address_to_u64(start_address)),
            head_address: AtomicU64::new(address_to_u64(start_address)),
            read_only_address: AtomicU64::new(address_to_u64(start_address)),
            tail_page_offset: AtomicPageOffset::new(
                get_page(start_address),
                get_offset(start_address),
            ),
            epoch,
            storage: Arc::new(Mutex::new(storage_device)),
            flushed_until_address: AtomicU64::new(address_to_u64(start_address)),
        };

        // Allocate the first page
        hlog.allocate_page(get_page(start_address))?;

        Ok(hlog)
    }

    /// Allocate space in the log for a record of given size
    /// Returns the address where the record can be written, or None if allocation fails
    pub fn allocate(&self, size: u32) -> Option<Address> {
        if size == 0 || size > PAGE_SIZE {
            return None;
        }

        loop {
            let (old_page, old_offset) = self.tail_page_offset.reserve(size);
            let new_offset = old_offset + size;

            if new_offset <= PAGE_SIZE {
                // Allocation fits in current page
                let address = make_address(old_page, old_offset);

                // Ensure the page is allocated
                if self.allocate_page(old_page).is_err() {
                    return None;
                }

                return Some(address);
            } else {
                // Need to move to next page
                let new_page = old_page + 1;
                if new_page > u32::MAX - 1 {
                    return None; // Address space exhausted
                }

                // Try to advance to the next page
                if self
                    .tail_page_offset
                    .compare_exchange(old_page, new_offset, new_page, size)
                    .is_ok()
                {
                    // Successfully moved to new page
                    if self.allocate_page(new_page).is_err() {
                        return None;
                    }

                    return Some(make_address(new_page, 0));
                }
                // If CAS failed, retry the allocation
            }
        }
    }

    /// Get a pointer to data at the specified address
    /// Returns a slice of the requested data if available in memory
    pub fn get(&self, address: Address) -> Option<&[u8]> {
        let page = get_page(address);
        let offset = get_offset(address);

        let page_index = (page % self.buffer_size) as usize;
        let page_guard = self.pages[page_index].read();

        if let Some(ref page_data) = *page_guard
            && (offset as usize) < page_data.len()
        {
            // SAFETY: We've verified the bounds above
            unsafe {
                let ptr = page_data.as_ptr().add(offset as usize);
                return Some(std::slice::from_raw_parts(
                    ptr,
                    page_data.len() - offset as usize,
                ));
            }
        }

        None
    }

    /// Write data to the log at the specified address
    pub fn write(&self, address: Address, data: &[u8]) -> Result<()> {
        let page = get_page(address);
        let offset = get_offset(address);

        if offset as usize + data.len() > PAGE_SIZE as usize {
            return Err(RsKvError::AllocationFailed {
                size: data.len() as u32,
            });
        }

        let page_index = (page % self.buffer_size) as usize;
        let mut page_guard = self.pages[page_index].write();

        if let Some(ref mut page_data) = *page_guard {
            let start = offset as usize;
            let end = start + data.len();

            if end <= page_data.len() {
                page_data[start..end].copy_from_slice(data);
                return Ok(());
            }
        }

        Err(RsKvError::AddressOutOfBounds { address })
    }

    /// Insert a record into the log
    pub fn insert_record(&self, record: LogRecord) -> Result<Address> {
        // Serialize the record
        let serialized = bincode::serialize(&record)?;
        let size = serialized.len() as u32;

        // Allocate space
        let address = self
            .allocate(size)
            .ok_or(RsKvError::AllocationFailed { size })?;

        // Write the serialized record
        self.write(address, &serialized)?;

        Ok(address)
    }

    /// Read a record from the log
    pub fn read_record(&self, address: Address) -> Result<LogRecord> {
        // First, try to read from memory
        if let Some(data) = self.get(address) {
            // Try to deserialize the record from memory
            match bincode::deserialize(data) {
                Ok(record) => return Ok(record),
                Err(_) => {
                    // Data might be truncated in memory buffer, try disk
                }
            }
        }

        // If not in memory or incomplete, read from disk
        self.read_record_from_disk(address)
    }

    /// Read a record from disk storage
    fn read_record_from_disk(&self, address: Address) -> Result<LogRecord> {
        // For this implementation, we'll read a fixed buffer size and try to deserialize
        const INITIAL_READ_SIZE: usize = 1024; // Start with 1KB
        const MAX_RECORD_SIZE: usize = 64 * 1024; // Max 64KB per record

        let storage = self.storage.lock();
        let mut buffer = vec![0u8; INITIAL_READ_SIZE];

        // Read initial chunk
        let bytes_read = storage.read(address, &mut buffer)?;
        if bytes_read == 0 {
            return Err(RsKvError::AddressOutOfBounds { address });
        }

        // Try to deserialize with initial buffer
        match bincode::deserialize::<LogRecord>(&buffer[..bytes_read]) {
            Ok(record) => Ok(record),
            Err(_) => {
                // Buffer might be too small, try with larger buffer
                let mut large_buffer = vec![0u8; MAX_RECORD_SIZE];
                let large_bytes_read = storage.read(address, &mut large_buffer)?;

                if large_bytes_read == 0 {
                    return Err(RsKvError::AddressOutOfBounds { address });
                }

                bincode::deserialize(&large_buffer[..large_bytes_read])
                    .map_err(RsKvError::Serialization)
            }
        }
    }

    /// Allocate a page in the buffer
    fn allocate_page(&self, page: u32) -> Result<()> {
        let page_index = (page % self.buffer_size) as usize;

        let mut page_guard = self.pages[page_index].write();
        if page_guard.is_none() {
            // Allocate the page
            let page_data = vec![0u8; PAGE_SIZE as usize].into_boxed_slice();
            *page_guard = Some(page_data);

            // Update status
            let mut status_guard = self.page_status[page_index].write();
            *status_guard = PageStatus::InMemory;
        }

        Ok(())
    }

    /// Shift the read-only address to the current tail
    /// This makes all current mutable data read-only
    pub fn shift_read_only_address(&self) -> Address {
        let tail_address = self.get_tail_address();
        let old_read_only = self
            .read_only_address
            .swap(address_to_u64(tail_address), Ordering::AcqRel);
        u64_to_address(old_read_only)
    }

    /// Shift the head address forward
    /// This removes pages from memory and makes them disk-only
    pub fn shift_head_address(&self, new_head_address: Address) -> Result<()> {
        let old_head = self
            .head_address
            .swap(address_to_u64(new_head_address), Ordering::AcqRel);
        let old_head_address = u64_to_address(old_head);

        // Evict pages that are now below the head address
        self.evict_pages_below_head(old_head_address, new_head_address)?;

        log::debug!(
            "Shifted head address from 0x{:x} to 0x{:x}",
            old_head_address,
            new_head_address
        );

        Ok(())
    }

    /// Evict pages from memory that are now below the head address
    fn evict_pages_below_head(&self, old_head: Address, new_head: Address) -> Result<()> {
        let old_head_page = get_page(old_head);
        let new_head_page = get_page(new_head);

        // Evict all pages between old_head and new_head
        for page in old_head_page..new_head_page {
            self.evict_page(page)?;
        }

        Ok(())
    }

    /// Evict a specific page from memory
    fn evict_page(&self, page: u32) -> Result<()> {
        let page_index = (page % self.buffer_size) as usize;

        // Lock the page and set status to OnDisk
        {
            let mut page_guard = self.pages[page_index].write();
            let mut status_guard = self.page_status[page_index].write();

            if *status_guard == PageStatus::InMemory {
                // Free the page memory
                *page_guard = None;
                *status_guard = PageStatus::OnDisk;

                log::trace!("Evicted page {page} from memory");
            }
        }

        Ok(())
    }

    /// Get current tail address
    pub fn get_tail_address(&self) -> Address {
        let (page, offset) = self.tail_page_offset.load();
        make_address(page, offset)
    }

    /// Get current head address
    pub fn get_head_address(&self) -> Address {
        u64_to_address(self.head_address.load(Ordering::Acquire))
    }

    /// Get current read-only address
    pub fn get_read_only_address(&self) -> Address {
        u64_to_address(self.read_only_address.load(Ordering::Acquire))
    }

    /// Get current begin address
    pub fn get_begin_address(&self) -> Address {
        u64_to_address(self.begin_address.load(Ordering::Acquire))
    }

    /// Advance the begin address and truncate the log
    /// This permanently removes data from storage and reclaims space
    pub fn advance_begin_address(&self, new_begin_address: Address) -> Result<u64> {
        let old_begin = self
            .begin_address
            .swap(address_to_u64(new_begin_address), Ordering::AcqRel);
        let old_begin_address = u64_to_address(old_begin);

        if new_begin_address <= old_begin_address {
            // Nothing to truncate
            return Ok(0);
        }

        // Calculate how many bytes we're reclaiming
        let bytes_reclaimed = new_begin_address.saturating_sub(old_begin_address);

        // Perform actual storage truncation
        self.truncate_storage(old_begin_address, new_begin_address)?;

        log::info!(
            "Advanced begin address from 0x{:x} to 0x{:x}, reclaimed {} bytes",
            old_begin_address,
            new_begin_address,
            bytes_reclaimed
        );

        Ok(bytes_reclaimed)
    }

    /// Truncate storage by removing data before the new begin address
    fn truncate_storage(&self, old_begin: Address, new_begin: Address) -> Result<()> {
        let mut storage = self.storage.lock();

        // For memory-mapped files, we can't actually truncate from the beginning
        // Instead, we mark the space as invalid and potentially compact later
        if storage.supports_mmap() {
            // For mmap devices, we use a different strategy
            self.mark_space_invalid(old_begin, new_begin)?;
        } else {
            // For regular file devices, we can perform actual truncation
            // by copying remaining data to the beginning of the file
            self.compact_storage(&mut **storage, old_begin, new_begin)?;
        }

        Ok(())
    }

    /// Mark space as invalid for memory-mapped storage
    fn mark_space_invalid(&self, _old_begin: Address, _new_begin: Address) -> Result<()> {
        // For now, we just update the begin address
        // In a production system, this might involve:
        // 1. Marking pages as free in a free list
        // 2. Scheduling background compaction
        // 3. Using file hole punching (fallocate) on supported filesystems

        log::debug!("Marked address range as invalid (mmap storage)");
        Ok(())
    }

    /// Compact storage by moving data and truncating the file
    fn compact_storage(
        &self,
        storage: &mut dyn StorageDevice,
        old_begin: Address,
        new_begin: Address,
    ) -> Result<()> {
        const BUFFER_SIZE: usize = 1024 * 1024; // 1MB buffer
        let mut buffer = vec![0u8; BUFFER_SIZE];

        let total_size = storage.size();
        let truncate_amount = new_begin - old_begin;

        if new_begin >= total_size {
            // Truncating everything
            storage.truncate(0)?;
            return Ok(());
        }

        // Read data from new_begin onwards and write it to the beginning
        let mut read_offset = new_begin;
        let mut write_offset = 0u64;

        while read_offset < total_size {
            let bytes_to_read = BUFFER_SIZE.min((total_size - read_offset) as usize);
            let bytes_read = storage.read(read_offset, &mut buffer[..bytes_to_read])?;

            if bytes_read == 0 {
                break;
            }

            storage.write(write_offset, &buffer[..bytes_read])?;

            read_offset += bytes_read as u64;
            write_offset += bytes_read as u64;
        }

        // Truncate file to new size
        let new_size = total_size - truncate_amount;
        storage.truncate(new_size)?;
        storage.flush()?;

        log::debug!(
            "Compacted storage: removed {} bytes, new size: {} bytes",
            truncate_amount,
            new_size
        );

        Ok(())
    }

    /// Flush data to storage device
    pub async fn flush_to_disk(&self, until_address: Address) -> Result<()> {
        let current_flushed = u64_to_address(self.flushed_until_address.load(Ordering::Acquire));

        if until_address <= current_flushed {
            // Already flushed
            return Ok(());
        }

        log::debug!(
            "Flushing data from 0x{:x} to 0x{:x}",
            current_flushed,
            until_address
        );

        // Flush page by page
        let start_page = get_page(current_flushed);
        let end_page = get_page(until_address);

        for page in start_page..=end_page {
            self.flush_page_to_disk(page).await?;
        }

        // Update flushed address
        self.flushed_until_address
            .store(address_to_u64(until_address), Ordering::Release);

        // Ensure storage device commits the data
        {
            let mut storage = self.storage.lock();
            storage.flush()?;
        }

        log::debug!("Flush completed to address 0x{until_address:x}");
        Ok(())
    }

    /// Flush a specific page to disk
    async fn flush_page_to_disk(&self, page: u32) -> Result<()> {
        let page_index = (page % self.buffer_size) as usize;

        // Get page data under lock
        let page_data = {
            let page_guard = self.pages[page_index].read();
            let status_guard = self.page_status[page_index].read();

            if *status_guard != PageStatus::InMemory {
                // Page not in memory or already flushed
                return Ok(());
            }

            if let Some(ref data) = *page_guard {
                data.clone()
            } else {
                return Ok(()); // No data to flush
            }
        };

        // Calculate disk offset for this page
        let disk_offset = (page as u64) * (PAGE_SIZE as u64);

        // Write to storage device (this is the potentially slow operation)
        {
            let mut storage = self.storage.lock();
            storage.write(disk_offset, &page_data)?;
        }

        // Update page status to indicate it's been flushed
        {
            let mut status_guard = self.page_status[page_index].write();
            if *status_guard == PageStatus::InMemory {
                *status_guard = PageStatus::Flushing; // Mark as flushing
            }
        }

        log::trace!(
            "Flushed page {} to disk at offset 0x{:x}",
            page,
            disk_offset
        );
        Ok(())
    }
}

/// Memory-mapped storage device for high-performance large file access
pub struct MmapStorageDevice {
    file: File,
    mmap: Option<MmapMut>,
    #[allow(dead_code)]
    path: PathBuf,
    size: u64,
    dirty: bool,
}

impl MmapStorageDevice {
    /// Create a new memory-mapped storage device
    pub fn new<P: AsRef<Path>>(path: P) -> Result<Self> {
        let path = path.as_ref().to_path_buf();
        let file = OpenOptions::new()
            .create(true)
            .truncate(true)
            .read(true)
            .write(true)
            .open(&path)?;

        let metadata = file.metadata()?;
        let size = metadata.len();

        let mut device = Self {
            file,
            mmap: None,
            path,
            size,
            dirty: false,
        };

        // Initialize memory mapping if file is not empty
        if size > 0 {
            device.init_mmap()?;
        }

        Ok(device)
    }

    /// Initialize memory mapping for the current file size
    fn init_mmap(&mut self) -> Result<()> {
        if self.size > 0 {
            let mmap = unsafe {
                MmapOptions::new()
                    .len(self.size as usize)
                    .map_mut(&self.file)?
            };
            self.mmap = Some(mmap);
        }
        Ok(())
    }

    /// Resize the file and remmap if necessary
    fn resize_and_remap(&mut self, new_size: u64) -> Result<()> {
        if new_size != self.size {
            // Drop old mapping
            self.mmap = None;

            // Resize file
            self.file.set_len(new_size)?;
            self.size = new_size;

            // Create new mapping
            if new_size > 0 {
                self.init_mmap()?;
            }
        }
        Ok(())
    }

    /// Ensure the file is large enough for the given offset + length
    fn ensure_capacity(&mut self, offset: u64, len: usize) -> Result<()> {
        let required_size = offset + len as u64;
        if required_size > self.size {
            // Grow file by at least 64MB chunks for efficiency
            const GROWTH_CHUNK: u64 = 64 * 1024 * 1024;
            let new_size = required_size.div_ceil(GROWTH_CHUNK) * GROWTH_CHUNK;
            self.resize_and_remap(new_size)?;
        }
        Ok(())
    }
}

impl StorageDevice for MmapStorageDevice {
    fn write(&mut self, offset: u64, data: &[u8]) -> Result<()> {
        self.ensure_capacity(offset, data.len())?;

        if let Some(ref mut mmap) = self.mmap {
            let start = offset as usize;
            let end = start + data.len();

            if end <= mmap.len() {
                mmap[start..end].copy_from_slice(data);
                self.dirty = true;
                return Ok(());
            }
        }

        // Fallback to file I/O if mmap is not available or out of bounds
        use std::io::{Seek, SeekFrom, Write};
        self.file.seek(SeekFrom::Start(offset))?;
        self.file.write_all(data)?;
        Ok(())
    }

    fn read(&self, offset: u64, buf: &mut [u8]) -> Result<usize> {
        if let Some(ref mmap) = self.mmap {
            let start = offset as usize;
            let len = buf.len().min(mmap.len().saturating_sub(start));

            if len > 0 {
                buf[..len].copy_from_slice(&mmap[start..start + len]);
                return Ok(len);
            }
        }

        // Fallback to file I/O if mmap is not available
        use std::io::{Read, Seek, SeekFrom};
        let mut file = &self.file;
        file.seek(SeekFrom::Start(offset))?;
        Ok(file.read(buf)?)
    }

    fn flush(&mut self) -> Result<()> {
        if self.dirty {
            if let Some(ref mut mmap) = self.mmap {
                mmap.flush()?;
            }
            self.file.sync_all()?;
            self.dirty = false;
        }
        Ok(())
    }

    fn size(&self) -> u64 {
        self.size
    }

    fn truncate(&mut self, size: u64) -> Result<()> {
        self.resize_and_remap(size)?;
        Ok(())
    }

    fn supports_mmap(&self) -> bool {
        true
    }

    fn get_mmap(&mut self, offset: u64, len: usize) -> Result<Option<&mut [u8]>> {
        self.ensure_capacity(offset, len)?;

        if let Some(ref mut mmap) = self.mmap {
            let start = offset as usize;
            let end = start + len;

            if end <= mmap.len() {
                return Ok(Some(&mut mmap[start..end]));
            }
        }

        Ok(None)
    }
}

impl Drop for MmapStorageDevice {
    fn drop(&mut self) {
        let _ = self.flush();
    }
}

